import React, { useState, useEffect } from 'react';
import { Github, Linkedin, Mail, Code, BarChart, Zap, Send, AlertTriangle } from 'lucide-react';

// --- Customizable Variables ---
// Update these variables with your information
const MY_NAME = "Kusuma";
const MY_TITLE = "Data Analyst & Python Developer";
const MY_BIO =
  "Passionate about leveraging data to solve real-world problems. Experienced in Python, MySQL, and statistical analysis, with a focus on fraud detection and system optimization.";
const ACCENT_COLOR_CLASS = "sky"; // Change 'sky' to 'emerald', 'violet', or 'rose' to change theme

// --- Mock Data (Will be replaced by your API data) ---
// This array is used while the API is loading.
const MOCK_PROJECTS = [
  {
    id: 1,
    title: "Transaction Fraud Pattern Analyzer",
    description: "A data analytics project utilizing Python and MySQL to identify and visualize fraudulent transaction patterns within large datasets.",
    tags: ["Python", "MySQL", "Data Analysis", "Pandas"],
    githubUrl: "[https://github.com/kusuma-13/P2](https://github.com/kusuma-13/P2)",
    demoUrl: "#",
    icon: BarChart,
  },
  {
    id: 2,
    title: "Transaction Fraud Pattern Analyzer (Visualization)",
    description: "A data analytics project for analyzing fraud transactions, focusing on identifying key behavioral and transactional anomalies and presenting them visually.",
    tags: ["Python", "MySQL", "Jupyter", "Visualization"],
    githubUrl: "[https://github.com/kusuma-13/P2](https://github.com/kusuma-13/P2)",
    demoUrl: "#",
    icon: BarChart,
  },
  {
    id: 3,
    title: "Local API Microservice",
    description: "A lightweight RESTful API built with Node.js and Express to manage user accounts and simple CRUD operations.",
    tags: ["Node.js", "Express", "REST API", "MongoDB"],
    githubUrl: "#",
    demoUrl: "#",
    icon: Code,
  },
];

// --- Helper Functions ---
const smoothScroll = (id) => {
  const element = document.getElementById(id);
  if (element) {
    element.scrollIntoView({ behavior: 'smooth' });
  }
};

// --- Component: Project Card ---
const ProjectCard = ({ project, accentColor }) => {
  const IconComponent = project.icon || Code;
  const color500 = `${accentColor}-500`;

  // Note: Tailwind classes like text-sky-500 are safe because 'sky' is defined in the constant.
  return (
    <div className={`bg-gray-800 p-6 rounded-lg shadow-xl hover:shadow-2xl transition-all duration-300 transform hover:-translate-y-1 border border-transparent hover:border-${color500}/50`}>
      <div className="flex items-start justify-between">
        <h3 className="text-xl font-bold mb-2 text-white">{project.title}</h3>
        <IconComponent className={`w-6 h-6 text-${color500}`} />
      </div>
      <p className="text-gray-400 mb-4 text-sm min-h-[60px]">{project.description}</p>
      
      <div className="flex flex-wrap gap-2 mb-4">
        {project.tags.map((tag, index) => (
          <span
            key={index}
            className={`text-xs font-medium px-2.5 py-0.5 rounded-full bg-${accentColor}-900 text-${accentColor}-300`}
          >
            {tag}
          </span>
        ))}
      </div>

      <div className="flex space-x-4 mt-4">
        <a
          href={project.githubUrl}
          target="_blank"
          rel="noopener noreferrer"
          className={`flex items-center text-${color500} hover:text-${accentColor}-400 transition-colors duration-200 text-sm`}
        >
          <Github className="w-4 h-4 mr-1" />
          GitHub
        </a>
        <a
          href={project.demoUrl}
          target="_blank"
          rel="noopener noreferrer"
          className={`flex items-center text-gray-400 hover:text-white transition-colors duration-200 text-sm ${project.demoUrl === '#' ? 'opacity-50 cursor-not-allowed' : ''}`}
        >
          <Zap className="w-4 h-4 mr-1" />
          Live Demo
        </a>
      </div>
    </div>
  );
};

// --- Component: Header/Navigation ---
const Header = ({ name, accentColor }) => {
  const color500 = `${accentColor}-500`;
  const navItems = [
    { name: "Home", id: "home" },
    { name: "Projects", id: "projects" },
    { name: "Contact", id: "contact" },
  ];

  return (
    <header className="fixed top-0 left-0 right-0 z-50 bg-gray-900/90 backdrop-blur-sm shadow-lg">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-16">
          <div className="flex-shrink-0">
            <a 
              href="#home" 
              onClick={() => smoothScroll('home')} 
              className={`text-2xl font-extrabold tracking-tight text-white hover:text-${color500} transition-colors duration-200`}
            >
              {name.split(' ')[0]}<span className={`text-${color500}`}>.dev</span>
            </a>
          </div>
          <nav className="hidden md:flex space-x-8">
            {navItems.map((item) => (
              <button
                key={item.id}
                onClick={() => smoothScroll(item.id)}
                className={`text-gray-300 hover:text-${color500} transition-colors duration-200 font-medium`}
              >
                {item.name}
              </button>
            ))}
          </nav>
        </div>
      </div>
    </header>
  );
};

// --- Main Application Component ---
const App = () => {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [contactForm, setContactForm] = useState({ name: '', email: '', message: '' });
  const [formStatus, setFormStatus] = useState(null); // null, 'success', 'error', 'submitting'

  // Fetch projects from the backend
  useEffect(() => {
    const fetchProjects = async () => {
      try {
        // NOTE: In a real-world app, replace this with a secure API call.
        const response = await fetch('http://localhost:5000/api/projects'); 
        const data = await response.json();
        setProjects(data);
      } catch (error) {
        console.error("Error fetching projects from API:", error);
        setProjects(MOCK_PROJECTS); // Fallback to mock data if API fails
      } finally {
        setLoading(false);
      }
    };
    fetchProjects();
  }, []);

  // Handle contact form submission
  const handleContactSubmit = async (e) => {
    e.preventDefault();
    setFormStatus('submitting');
    
    try {
      const response = await fetch('http://localhost:5000/api/contact', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(contactForm),
      });

      if (response.ok) {
        setFormStatus('success');
        setContactForm({ name: '', email: '', message: '' }); // Clear form
      } else {
        const errorData = await response.json();
        setFormStatus(`error: ${errorData.message || 'Server error occurred.'}`);
      }
    } catch (error) {
      setFormStatus('error: Could not connect to the backend server.');
    } finally {
      // Clear status after a short delay
      setTimeout(() => setFormStatus(null), 5000);
    }
  };

  const color500 = `${ACCENT_COLOR_CLASS}-500`;
  const color400 = `${ACCENT_COLOR_CLASS}-400`;

  return (
    <div className="min-h-screen bg-gray-900 text-gray-100 font-sans antialiased">
      <Header name={MY_NAME} accentColor={ACCENT_COLOR_CLASS} />

      {/* --- Home/Hero Section --- */}
      <section id="home" className="pt-24 pb-16 min-h-screen flex items-center">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 flex flex-col md:flex-row items-center space-y-12 md:space-y-0 md:space-x-12">
          
          <div className="flex-1 text-center md:text-left">
            <h1 className="text-4xl sm:text-5xl lg:text-7xl font-extrabold tracking-tight text-white mb-4">
              Hi, I'm <span className={`text-${color500}`}>{MY_NAME}</span>
            </h1>
            <h2 className={`text-xl sm:text-2xl lg:text-3xl font-semibold mb-6 text-${color400}`}>
              {MY_TITLE}
            </h2>
            <p className="text-gray-300 max-w-2xl text-lg mb-8">
              {MY_BIO}
            </p>
            <div className="flex justify-center md:justify-start space-x-4">
              <a
                href="#contact"
                onClick={() => smoothScroll('contact')}
                className={`px-6 py-3 font-semibold rounded-lg bg-${color500} text-white hover:bg-${ACCENT_COLOR_CLASS}-600 transition-colors duration-200 shadow-lg`}
              >
                Get In Touch
              </a>
              <a
                href="[https://github.com/kusuma-13](https://github.com/kusuma-13)"
                target="_blank"
                rel="noopener noreferrer"
                className="px-6 py-3 font-semibold rounded-lg border border-gray-700 text-gray-300 hover:text-white hover:bg-gray-800 transition-colors duration-200"
              >
                <Github className="w-5 h-5 inline mr-2" /> GitHub
              </a>
            </div>
          </div>

          <div className="flex-shrink-0">
            <div className={`w-64 h-64 sm:w-80 sm:h-80 rounded-full overflow-hidden bg-gray-700 flex items-center justify-center border-4 border-${color500}`}>
              {/* Profile Image Placeholder */}
              <svg className={`w-3/4 h-3/4 text-${color400}`} fill="currentColor" viewBox="0 0 24 24">
                <path d="M12 12c2.21 0 4-1.79 4-4s-1.79-4-4-4-4 1.79-4 4 1.79 4 4 4zm0 2c-2.67 0-8 1.34-8 4v2h16v-2c0-2.66-5.33-4-8-4z"/>
              </svg>
            </div>
          </div>
        </div>
      </section>

      {/* --- Projects Section --- */}
      <section id="projects" className="py-20">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <h2 className={`text-4xl font-extrabold text-white text-center mb-12 border-b-2 border-${color500} inline-block pb-1`}>
            Projects
          </h2>
          
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
            {loading ? (
              <div className="text-center md:col-span-3">Loading projects...</div>
            ) : (
              projects.length > 0 ? (
                projects.map((project) => (
                  <ProjectCard key={project.id} project={project} accentColor={ACCENT_COLOR_CLASS} />
                ))
              ) : (
                <div className="text-center md:col-span-3 text-red-400 flex items-center justify-center">
                  <AlertTriangle className="w-5 h-5 mr-2" />
                  No projects found. Check your backend API connection.
                </div>
              )
            )}
          </div>
        </div>
      </section>

      {/* --- Contact Section --- */}
      <section id="contact" className="py-20 bg-gray-800">
        <div className="max-w-xl mx-auto px-4 sm:px-6 lg:px-8">
          <h2 className={`text-4xl font-extrabold text-white text-center mb-12 border-b-2 border-${color500} inline-block pb-1`}>
            Get In Touch
          </h2>
          <form onSubmit={handleContactSubmit} className="space-y-6">
            
            <div>
              <label htmlFor="name" className="block text-sm font-medium text-gray-300">Name</label>
              <input
                type="text"
                id="name"
                value={contactForm.name}
                onChange={(e) => setContactForm({ ...contactForm, name: e.target.value })}
                required
                className="mt-1 block w-full px-4 py-3 bg-gray-700 border border-gray-600 rounded-lg text-white placeholder-gray-400 focus:ring-sky-500 focus:border-sky-500 transition-colors"
              />
            </div>
            
            <div>
              <label htmlFor="email" className="block text-sm font-medium text-gray-300">Email</label>
              <input
                type="email"
                id="email"
                value={contactForm.email}
                onChange={(e) => setContactForm({ ...contactForm, email: e.target.value })}
                required
                className="mt-1 block w-full px-4 py-3 bg-gray-700 border border-gray-600 rounded-lg text-white placeholder-gray-400 focus:ring-sky-500 focus:border-sky-500 transition-colors"
              />
            </div>
            
            <div>
              <label htmlFor="message" className="block text-sm font-medium text-gray-300">Message</label>
              <textarea
                id="message"
                rows="4"
                value={contactForm.message}
                onChange={(e) => setContactForm({ ...contactForm, message: e.target.value })}
                required
                className="mt-1 block w-full px-4 py-3 bg-gray-700 border border-gray-600 rounded-lg text-white placeholder-gray-400 focus:ring-sky-500 focus:border-sky-500 transition-colors"
              ></textarea>
            </div>
            
            {formStatus && (
              <div className={`p-3 rounded-lg text-sm font-medium ${
                formStatus.includes('success') ? 'bg-green-500 text-white' : 'bg-red-500 text-white'
              }`}>
                {formStatus === 'submitting' ? 'Sending message...' : formStatus.includes('success') ? 'Message sent successfully!' : `Error: ${formStatus.split(': ')[1] || 'Submission failed.'}`}
              </div>
            )}

            <button
              type="submit"
              disabled={formStatus === 'submitting'}
              className={`w-full flex justify-center items-center px-6 py-3 border border-transparent text-base font-medium rounded-lg text-white bg-${color500} hover:bg-${ACCENT_COLOR_CLASS}-600 transition-colors duration-200 disabled:opacity-50 disabled:cursor-not-allowed`}
            >
              <Send className="w-5 h-5 mr-2" />
              {formStatus === 'submitting' ? 'Sending...' : 'Send Message'}
            </button>
          </form>
        </div>
      </section>

      {/* --- Footer --- */}
      <footer className="bg-gray-900 border-t border-gray-700/50 py-8">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 flex flex-col sm:flex-row justify-between items-center text-sm text-gray-400">
          <p>&copy; {new Date().getFullYear()} {MY_NAME}. All rights reserved.</p>
          <div className="flex space-x-4 mt-4 sm:mt-0">
            <a href="[https://github.com/kusuma-13](https://github.com/kusuma-13)" target="_blank" rel="noopener noreferrer" className={`hover:text-${color500} transition-colors duration-200`}>
              <Github className="w-5 h-5" />
            </a>
            <a href="[https://www.linkedin.com/in/kusuma-v](https://www.linkedin.com/in/kusuma-v)" target="_blank" rel="noopener noreferrer" className={`hover:text-${color500} transition-colors duration-200`}>
              <Linkedin className="w-5 h-5" />
            </a>
            <a href="mailto:kusumavkusumav13@gmail.com" className={`hover:text-${color500} transition-colors duration-200`}>
              <Mail className="w-5 h-5" />
            </a>
          </div>
        </div>
      </footer>
    </div>
  );
};

export default App;
