import React, { useState } from 'react';
import { createRoot } from 'react-dom/client';
// CRITICAL FIX: All icons used anywhere in this file must be imported here.
import { 
  Github, Linkedin, Mail, Code, BarChart, Zap, Send, 
  GraduationCap, HardHat, Briefcase, Award, Phone, Home, User, Menu, X 
} from 'lucide-react'; 

// --- Customizable Variables (Personal Information) ---
const MY_NAME = "Kusuma V";
const MY_TITLE = "Data Analyst Intern";
const MY_BIO =
  "Passionate about leveraging data to solve real-world problems. Experienced in Python, MySQL, and statistical analysis, with a focus on fraud detection and system optimization.";
// CONFIRMED: Using the email provided by the user.
const MY_EMAIL = "kusumavkusumav13@gmail.com"; 
const MY_PHONE = "+91 99026 41774"; 

const PROFILE_IMAGE_URL = "https://github.com/kusuma-13/FUTURE_FS_01/blob/main/frontend/src/assets/profile_photo.jpg?raw=true"; 
// Theme changed to 'emerald' and dark mode classes applied
const ACCENT_COLOR_CLASS = "emerald"; 

// --- Mock Data ---

// 1. EDUCATION 
const EDUCATION = [
    {
        degree: "Bachelor of Computer Applications (BCA)",
        institution: "MLA Academy of Higher Learning, Bengaluru",
        dates: "2023 - 2026 (Expected)",
        details: ["Relevant Coursework: Data Structures, Database Management Systems, Advanced Algorithms, and Web Development Principles."],
    },
    {
        degree: "Pre-University Certification (PUC) - Commerce Stream",
        institution: "JS Pre-University College, Bengaluru",
        dates: "2021 - 2023",
        details: [
            "Core Subjects: **Business Studies**, **Accountancy**, **Statistics**, and **Computer Science**.",
            "Established a strong foundation in commercial operations, financial analysis, and quantitative methods."
        ],
    },
];

// 2. EXPERIENCE
const EXPERIENCE = [
  {
    title: "Data Analytics Intern",
    company: "Rooman Technologies Pvt. Ltd.",
    dates: "September 2025 (1 Month)",
    description: [
      "Assisted in cleaning and preprocessing raw datasets using **Pandas** and **NumPy**, improving data reliability by 15%.",
      "Developed interactive dashboards using **Power BI** for the sales team, reducing report generation time by 2 hours daily.",
      "Wrote and optimized **MySQL** queries for complex data extraction, supporting a system migration project.",
      "Contributed to data quality checks using **Streamlit** to build a lightweight data validation application."
    ],
  },
];

// 3. CERTIFICATIONS
const certifications = [
  { name: "Power BI", issuer: "SimpliLearn", date: "August 2025" },
  { name: "Cloud Application Development", issuer: "Skill India (PMKVY)", date: "Mar 2024" },
  { name: "Digital Marketing Short-term Course", issuer: "Inspyr", date: "April 2024" },
  { name: "Office Automation", issuer: "KSEAB", date: "July 2023" },
];

// 4. PROJECTS
const projects = [
  {
    title: "Transaction Fraud Pattern Analyzer",
    description: "A data analytics project utilizing Python and MySQL to identify and visualize fraudulent transaction patterns within large datasets.",
    tags: ["Python", "MySQL", "Data Analysis", "Pandas"],
    link: "https://github.com/kusuma-13/Beginner_Project.git",
  },
  {
    title: "Job Portal Resume Keyword Analyzer",
    description: "A data analytics project using Python and MYSQL to detect keywords in resumes for job portals, enhancing candidate-job matching efficiency.",
    tags: ["Python", "MYSQL", "Streamlit"],
    link: "https://github.com/kusuma-13/Intermediate_Project.git",
  },
];

// 5. SKILLS - Updated based on projects
const skills = [
    {
        title: "Core Data & Project Skills",
        icon: BarChart,
        tags: ["Python", "Pandas", "NumPy", "MySQL (SQL)", "Data Analysis", "Fraud Detection"],
    },
    {
        title: "Data Visualization & Reporting",
        icon: Zap, 
        tags: ["Power BI", "Streamlit", "Statistical Analysis", "Dashboarding", "Keyword Analysis"],
    },
    {
        title: "Development & Foundations",
        icon: Code,
        tags: ["Git & GitHub", "Jupyter Notebooks", "VS Code", "HTML/CSS (Tailwind)", "Cloud Application Development"],
    },
];

// --- Navigation Data (Section order) ---
const NAV_LINKS = [
    { name: "Home", id: "home", icon: Home },
    { name: "About Me", id: "about", icon: User }, 
    { name: "Education & Qualification", id: "education", icon: GraduationCap },
    { name: "Internship Experience", id: "experience", icon: Briefcase },
    { name: "Projects", id: "projects", icon: HardHat }, 
    { name: "Skills", id: "skills", icon: Zap }, 
    { name: "Certifications", id: "certifications", icon: Award },
    { name: "Contact", id: "contact", icon: Send },
];


// --- Shared Scroll Function ---
const scrollToSection = (id) => {
    const element = document.getElementById(id);
    if (element) {
        // Scroll slightly above the element to account for the fixed nav bar
        const offset = element.offsetTop - 80; 
        window.scrollTo({
            top: offset,
            behavior: 'smooth'
        });
    }
};

// --- Component Definitions (UI Building Blocks) ---

const NavBar = ({ accentColor }) => {
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    
    // Updated for Dark Theme
    return (
        <nav className={`fixed top-0 left-0 right-0 z-50 bg-gray-900 border-b border-gray-700 shadow-lg`}>
            <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8">
                <div className="flex justify-between items-center h-16">
                    <div className={`flex-shrink-0 text-xl font-bold text-${accentColor}-400`}>
                        {MY_NAME}
                    </div>
                    {/* Desktop Navigation */}
                    <div className="hidden md:flex space-x-4">
                        {NAV_LINKS.map(link => (
                            <button
                                key={link.id}
                                onClick={() => scrollToSection(link.id)}
                                className={`text-gray-300 hover:bg-gray-700 hover:text-${accentColor}-400 px-3 py-2 rounded-md text-sm font-medium transition duration-150`}
                            >
                                {link.name}
                            </button>
                        ))}
                    </div>
                    {/* Mobile Menu Button */}
                    <div className="md:hidden">
                        <button
                            onClick={() => setIsMenuOpen(!isMenuOpen)}
                            className={`p-2 rounded-md text-gray-300 hover:text-${accentColor}-400 hover:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-gray-900 focus:ring-${accentColor}-400`}
                        >
                            <svg className="h-6 w-6" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                {isMenuOpen ? <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" /> : <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 6h16M4 12h16M4 18h16" />}
                            </svg>
                        </button>
                    </div>
                </div>
            </div>

            {/* Mobile Menu */}
            {isMenuOpen && (
                <div className="md:hidden border-t border-gray-700">
                    <div className="px-2 pt-2 pb-3 space-y-1 sm:px-3">
                        {NAV_LINKS.map(link => (
                            <button
                                key={link.id}
                                onClick={() => {
                                    scrollToSection(link.id);
                                    setIsMenuOpen(false);
                                }}
                                className={`block w-full text-left text-gray-300 hover:bg-gray-700 hover:text-${accentColor}-400 px-3 py-2 rounded-md text-base font-medium transition duration-150 flex items-center`}
                            >
                                <link.icon className="h-4 w-4 mr-2" />
                                {link.name}
                            </button>
                        ))}
                    </div>
                </div>
            )}
        </nav>
    );
};


// MODIFIED HEADER: Profile photo size increased to w-40 h-40
const Header = ({ name, title, imageUrl, accentColor }) => (
  <header 
    id="home" 
    className="flex flex-col sm:flex-row-reverse items-center justify-between p-6 bg-gray-800 shadow-2xl rounded-lg mb-8 pt-24 md:pt-10 border border-gray-700"
  >
    <div className="flex-shrink-0 mb-4 sm:mb-0 sm:ml-6">
      <img
        // Increased photo size
        className={`w-40 h-40 rounded-full object-cover border-4 border-${accentColor}-400 shadow-md`}
        src={imageUrl}
        alt={`${name}'s profile`}
        onError={(e) => { e.target.onerror = null; e.target.src = "https://placehold.co/160x160/1f2937/d1d5db?text=KV"; }}
      />
    </div>

    <div className="flex-grow text-center sm:text-left">
      <div className="mb-4">
        {/* NAME: Large size maintained */}
        <h1 className="text-5xl sm:text-6xl font-extrabold text-gray-100 tracking-tight">{name}</h1>
        <p className={`text-xl font-medium text-${accentColor}-400 mt-1`}>{title}</p>
      </div>
    </div>
  </header>
);

// Section component now ensures w-full and correctly manages margin-top
const Section = ({ title, icon: Icon, accentColor, children, id, className = "" }) => (
  <section 
    id={id} 
    className={`p-6 bg-gray-800 rounded-lg shadow-lg w-full border border-gray-700 mt-8 ${className}`}
  >
    <h2 className={`text-2xl font-bold border-b-2 border-${accentColor}-500 pb-2 text-gray-100 flex items-center mb-4`}>
        <Icon className={`h-6 w-6 mr-2 text-${accentColor}-400`} />
        {title}
    </h2>
    {children}
  </section>
);

// NEW/REVERTED ABOUT ME SECTION
const AboutMe = ({ bio, accentColor }) => (
    <Section id="about" title="About Me" icon={User} accentColor={accentColor} className="mt-8">
        <p className="text-gray-300 text-base italic font-semibold">{bio}</p>
    </Section>
)


const Contact = ({ accentColor, email, phone }) => (
  <Section id="contact" title="Contact" icon={Send} accentColor={accentColor}>
    {/* Responsive grid layout for contact information */}
    <div className="grid grid-cols-1 md:grid-cols-2 gap-6 text-gray-300 text-base">
        
        {/* Email - Confirmed and updated */}
        <a href={`mailto:${email}`} 
           className={`flex items-center p-3 rounded-lg bg-gray-700 border border-gray-600 hover:bg-gray-600 hover:text-${accentColor}-400 transition font-medium shadow-md`}
        >
            <Mail className={`h-5 w-5 mr-3 text-${accentColor}-400`} />
            {email}
        </a>

        {/* Phone */}
        <a href={`tel:${phone.replace(/\s/g, '')}`} 
           className={`flex items-center p-3 rounded-lg bg-gray-700 border border-gray-600 hover:bg-gray-600 hover:text-${accentColor}-400 transition font-medium shadow-md`}
        >
            <Phone className={`h-5 w-5 mr-3 text-${accentColor}-400`} />
            {phone}
        </a>

        {/* LinkedIn */}
        <a href="https://linkedin.com/in/kusuma-v" target="_blank" rel="noopener noreferrer" 
           className={`flex items-center p-3 rounded-lg bg-gray-700 border border-gray-600 hover:bg-gray-600 hover:text-${accentColor}-400 transition font-medium shadow-md`}
        >
            <Linkedin className={`h-5 w-5 mr-3 text-${accentColor}-400`} />
            LinkedIn Profile
        </a>

        {/* GitHub */}
        <a href="https://github.com/kusuma-13" target="_blank" rel="noopener noreferrer" 
           className={`flex items-center p-3 rounded-lg bg-gray-700 border border-gray-600 hover:bg-gray-600 hover:text-${accentColor}-400 transition font-medium shadow-md`}
        >
            <Github className={`h-5 w-5 mr-3 text-${accentColor}-400`} />
            GitHub Repository
        </a>
    </div>
  </Section>
);


const Experience = ({ experience, accentColor }) => (
  <Section id="experience" title="Internship Experience" icon={Briefcase} accentColor={accentColor}>
    <div className="grid grid-cols-1 gap-4">
      {experience.map((exp, index) => (
        // Updated for Dark Theme
        <div key={index} className={`mb-4 p-4 border-l-4 border-${accentColor}-600 hover:border-${accentColor}-400 transition duration-300 rounded-md bg-gray-700`}>
          <div className="flex justify-between items-start">
            <h3 className="text-xl font-semibold text-gray-200">{exp.title} at {exp.company}</h3>
            <span className={`text-sm font-medium text-${accentColor}-400`}>{exp.dates}</span>
          </div>
          <ul className="list-disc list-outside ml-5 mt-2 text-gray-300">
            {exp.description.map((item, i) => (
              <li key={i} className="text-sm mb-1" dangerouslySetInnerHTML={{ __html: item }} />
            ))}
          </ul>
        </div>
      ))}
    </div>
  </Section>
);

const Education = ({ education, accentColor }) => (
    <Section id="education" title="Education & Qualification" icon={GraduationCap} accentColor={accentColor}>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {education.map((edu, index) => (
                // Updated for Dark Theme
                <div key={index} className={`p-4 border-l-4 border-${accentColor}-600 hover:border-${accentColor}-400 transition duration-300 rounded-md bg-gray-700`}>
                    <div className="flex justify-between items-start">
                        <h3 className="text-lg font-semibold text-gray-200">{edu.degree}</h3>
                        <p className={`text-sm font-medium text-${accentColor}-400`}>{edu.dates}</p>
                    </div>
                    <p className="text-gray-300">{edu.institution}</p>
                    <ul className="list-disc list-outside ml-5 mt-2 text-gray-400 text-xs">
                        {edu.details.map((item, i) => (
                            <li key={i} className="mb-1" dangerouslySetInnerHTML={{ __html: item }} />
                        ))}
                    </ul>
                </div>
            ))}
        </div>
    </Section>
);

const Certifications = ({ certifications, accentColor }) => (
    <Section id="certifications" title="Certifications" icon={Award} accentColor={accentColor}>
        <div className="grid grid-cols-1 gap-4">
            {certifications.map((cert, index) => (
                // Updated for Dark Theme
                <div key={index} className={`p-4 rounded-md bg-gray-700 border-l-4 border-${accentColor}-600`}>
                    <h3 className="text-lg font-semibold text-gray-200">{cert.name}</h3>
                    <div className="flex justify-between text-sm text-gray-400">
                        <span>{cert.issuer}</span>
                        <span className={`font-medium text-${accentColor}-400`}>{cert.date}</span>
                    </div>
                </div>
            ))}
        </div>
    </Section>
);

const Projects = ({ projects, accentColor }) => (
  <Section id="projects" title="Projects" icon={HardHat} accentColor={accentColor}>
    {/* Single-column layout */}
    <div className="grid grid-cols-1 gap-6"> 
      {projects.map((proj, index) => (
        // Project cards are now full-width
        <div key={index} className={`p-4 bg-gray-700 rounded-lg shadow-sm border border-gray-600 hover:border-${accentColor}-400 transition`}>
          <h3 className={`text-xl font-semibold text-gray-100 mb-2 hover:text-${accentColor}-400 transition`}>
            <a href={proj.link} target="_blank" rel="noopener noreferrer">{proj.title}</a>
          </h3>
          <p className="text-gray-300 text-sm mb-3">{proj.description}</p>
          <div className="flex flex-wrap gap-2 pt-2">
            {proj.tags.map((tag, i) => (
              // Bright tag style for dark theme
              <span key={i} className={`bg-${accentColor}-600 text-white text-xs font-medium px-3 py-1 rounded-full shadow-md`}>
                {tag}
              </span>
            ))}
          </div>
        </div>
      ))}
    </div>
  </Section>
);

// MODIFIED SKILLS COMPONENT: Uses an outer 2-column grid for categories
const Skills = ({ skills, accentColor }) => (
  <Section id="skills" title="Skills" icon={Zap} accentColor={accentColor}>
    {/* Outer Grid for Skill Categories: 2 columns on medium screens and up */}
    <div className="grid grid-cols-1 md:grid-cols-2 gap-6"> 
      {skills.map((category, index) => {
        const CategoryIcon = category.icon; 
        return (
          // Category Card
          <div key={index} className={`bg-gray-700 p-4 rounded-lg border border-gray-600 shadow-xl`}>
            <h3 className={`text-lg font-bold text-gray-100 mb-4 pb-2 border-b border-gray-600 flex items-center`}>
              <CategoryIcon className={`h-5 w-5 mr-2 text-${accentColor}-400`} />
              {category.title}
            </h3>
            
            {/* Inner Grid for Card-style Skills (3 columns) */}
            <div className="grid grid-cols-3 gap-3">
              {category.tags.map((tag, i) => (
                // Skill Card Style
                <div key={i} 
                  className={`flex items-center justify-center p-2 text-center bg-gray-800 rounded-lg 
                             text-xs font-semibold text-gray-200 border border-gray-600 shadow-sm
                             hover:bg-${accentColor}-800 hover:text-white hover:border-${accentColor}-400 transition duration-200 transform hover:scale-[1.05]`}
                >
                  {tag}
                </div>
              ))}
            </div>
            
          </div>
        );
      })}
    </div>
  </Section>
);


// --- Main Application Component ---
export default function App() {
  return (
    // Main background is dark gray
    <div className="min-h-screen bg-gray-900 font-sans p-4 sm:p-8 pt-16"> 
        <NavBar accentColor={ACCENT_COLOR_CLASS} />
        
        {/* Main content container with dark theme styling */}
        <div id="resume-container" className="max-w-6xl mx-auto shadow-2xl rounded-xl p-4 sm:p-8 bg-gray-900">
            
            {/* 1. HOME (Name, Title, Photo) */}
            <Header 
              name={MY_NAME} 
              title={MY_TITLE} 
              accentColor={ACCENT_COLOR_CLASS} 
              imageUrl={PROFILE_IMAGE_URL} 
            />
            
            {/* 2. ABOUT ME (Bio) */}
            <AboutMe
                bio={MY_BIO}
                accentColor={ACCENT_COLOR_CLASS}
            />

            {/* 3. EDUCATION & QUALIFICATION */}
            <Education 
                education={EDUCATION}
                accentColor={ACCENT_COLOR_CLASS}
            />

            {/* 4. INTERNSHIP EXPERIENCE */}
            <Experience 
              experience={EXPERIENCE} 
              accentColor={ACCENT_COLOR_CLASS} 
            />
            
            {/* 5. PROJECTS (Stacked) */}
            <Projects 
                projects={projects} 
                accentColor={ACCENT_COLOR_CLASS} 
            />
            
            {/* 6. SKILLS (Stacked - Now with Card Layout) */}
            <Skills 
                skills={skills} 
                accentColor={ACCENT_COLOR_CLASS} 
            />
            
            {/* 7. CERTIFICATIONS (Stacked) */}
            <Certifications
                certifications={certifications}
                accentColor={ACCENT_COLOR_CLASS}
            />

            {/* 8. CONTACT */}
            <Contact 
              accentColor={ACCENT_COLOR_CLASS} 
              email={MY_EMAIL}
              phone={MY_PHONE}
            />

            {/* 9. FOOTER */}
            <footer className="bg-gray-900 py-6 border-t border-gray-700 mt-8 rounded-b-xl shadow-inner">
              <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center text-gray-500 text-sm">
                &copy; {new Date().getFullYear()} {MY_NAME}. Built with React and Tailwind CSS.
              </div>
            </footer>
        </div>
    </div>
  );
};

// Initial Render Logic for React
const container = document.getElementById('root');
if (container) {
  const root = createRoot(container);
  root.render(<App />);
}
