import React, { useState, useEffect } from 'react';
import { createRoot } from 'react-dom/client';
// CRITICAL FIX: All icons used anywhere in this file must be imported here.
import { 
  Github, Linkedin, Mail, Code, BarChart, Zap, Send, 
  AlertTriangle, GraduationCap, HardHat, ChevronsRight, Briefcase, 
  Menu, X, CheckCircle, Award, Lightbulb
} from 'lucide-react'; 

// --- Customizable Variables (Personal Information) ---
const MY_NAME = "Kusuma V";
const MY_TITLE = "Data Analyst Intern";
const MY_BIO =
  "Passionate about leveraging data to solve real-world problems. Experienced in Python, MySQL, and statistical analysis, with a focus on fraud detection and system optimization.";
// **************************************************************************************************************************************************
const PROFILE_IMAGE_URL = "https://placehold.co/400x400/273a5a/FFFFFF?text=KV+Profile"; // Using a stable placeholder image
// **************************************************************************************************************************************************
const ACCENT_COLOR_CLASS = "sky"; // Change 'sky' to 'emerald', 'violet', or 'rose' to change theme
const MY_EMAIL = "kusumavkusumav13@gmail.com";


// --- Mock Data (Replace with your actual data) ---

// 1. PROJECTS
const MOCK_PROJECTS = [
  {
    id: 1,
    title: "Transaction Fraud Pattern Analyzer",
    description: "A data analytics project utilizing Python and MySQL to identify and visualize fraudulent transaction patterns within large datasets.",
    tags: ["Python", "MySQL", "Data Analysis", "Pandas"],
    githubUrl: "https://github.com/kusuma-13/Beginner_Project.git",
    demoUrl: "#",
    icon: BarChart,
  },
  {
    id: 2,
    title: "Job Portal Resume Keyword Analyzer",
    description: "A data analytics project using Python and MYSQL to detect keywords in resumes for job portals, enhancing candidate-job matching efficiency.",
    tags: ["Python", "MYSQL", "Streamlit"],
    githubUrl: "https://github.com/kusuma-13/Intermediate_Project.git",
    demoUrl: "#",
    icon: Zap,
  },
];

// 2. INTERNSHIP EXPERIENCE
const MOCK_INTERNSHIPS = [
  {
    id: 1,
    type: 'experience', 
    company: "Rooman Technologies Pvt. Ltd.",
    role: "Data Analytics Intern",
    duration: "September 2025 (1 Month)",
    details: [
      "Assisted in cleaning and preprocessing raw datasets using **Pandas** and **NumPy**, improving data reliability by 15%.",
      "Developed interactive dashboards using **Power BI** for the sales team, reducing report generation time by 2 hours daily.",
      "Wrote and optimized **MySQL** queries for complex data extraction, supporting a system migration project.",
      "Contributed to data quality checks using **Streamlit** to build a lightweight data validation application."
    ],
  },
];

// 3. EDUCATION (Academic History)
const MOCK_EDUCATION = [
  {
    id: 1,
    type: 'education', 
    institution: "MLA Academy of Higher Learning",
    degree: "Bachelor of Computer Applications (BCA)",
    duration: "2023 - 2026 (Expected)",
    details: "Currently pursuing foundational knowledge in computer science, software development principles, and analytical skills. Focus on data structures and database management.",
    category: 'degree' // New category field
  },
  {
    id: 2,
    type: 'education', 
    institution: "JS Pre-University College",
    degree: "Pre-University Course (PUC) - Science",
    duration: "2021 - 2023",
    details: "Completed Pre-University course with a focus on Physics, Chemistry, Mathematics, and Computer Science.",
    category: 'puc' // New category field
  },
];

// 4. CERTIFICATIONS
const MOCK_CERTIFICATIONS = [
  { 
    id: 1, 
    name: "Power BI", 
    issuer: "SimpliLearn", 
    date: "August 2025",
    link: "#" // Placeholder for credential link
  },
  { 
    id: 2, 
    name: "Cloud Application Development", 
    issuer: "Skill India (PMKVY)", 
    date: "Mar 2024",
    link: "#"
  },
  { 
    id: 3, 
    name: "Digital Marketing Short-term Course", 
    issuer: "Inspyr", 
    date: "April 2024",
    link: "#"
  },
  { 
    id: 4, 
    name: "Office Automation", 
    issuer: "KSEAB", 
    date: "July 2023",
    link: "#"
  },
];

// 5. SKILLS (Core Technical Skills)
const MOCK_SKILLS = {
  "Programming & Scripting": ["Python (Pandas, NumPy)", "SQL (MySQL)"],
  "Data Visualization & BI": ["Power BI", "Streamlit", "Seaborn", "Matplotlib"],
  "Tools & Databases": ["Git/GitHub", "Jupyter Notebooks", "MySQL", "VS Code"],
  "Concepts": ["Statistical Analysis", "Data Cleaning", "Data Modeling"]
};


// --- Helper Functions and Components ---
const smoothScroll = (id) => {
  const element = document.getElementById(id);
  if (element) {
    element.scrollIntoView({ behavior: 'smooth' });
  }
};

// Component for a styled section header
const SectionHeader = ({ title, accentColor, Icon }) => (
    <h2 className={`text-3xl sm:text-4xl font-extrabold text-white text-center mb-12 border-b-2 border-${accentColor}-500 inline-block pb-2 px-4 flex items-center justify-center mx-auto`}>
      {Icon && <Icon className={`w-8 h-8 mr-3 text-${accentColor}-400`} />}
      {title}
    </h2>
);

// Component for Experience/Education Item (Timeline)
// Reusing this for the divided education section, now in a split layout
const TimelineItem = ({ item, accentColor }) => {
    const isExperience = item.type === 'experience';
    const primaryTitle = isExperience ? item.role : item.degree;
    const secondaryTitle = isExperience ? item.company : item.institution;
    const IconComponent = isExperience ? Briefcase : GraduationCap;

    return (
        <div className={`w-full p-4 md:p-6 bg-gray-800 rounded-xl shadow-lg border-l-4 border-${accentColor}-500 transition-all duration-300 hover:shadow-2xl`}>
            <div className="flex items-center justify-between mb-2">
                <h3 className="text-xl font-semibold text-white">{primaryTitle}</h3>
                <IconComponent className={`w-6 h-6 flex-shrink-0 text-${accentColor}-500`} />
            </div>

            <p className={`text-md font-medium text-${accentColor}-400 mt-1`}>{secondaryTitle}</p>
            <p className="text-sm text-gray-400 mt-1 mb-3">{item.duration}</p>
            
            {/* Details */}
            {item.details && (
                <ul className="text-gray-300 text-sm list-none pl-0 space-y-1 text-left">
                    {/* Check if details is an array or a string */}
                    {(Array.isArray(item.details) ? item.details : [item.details]).map((detail, detailIndex) => (
                        <li key={detailIndex} className="flex items-start">
                            <ChevronsRight className={`w-4 h-4 mt-1 mr-2 flex-shrink-0 text-${accentColor}-400`} />
                            <span dangerouslySetInnerHTML={{ __html: detail }}></span>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

// Skill Tag Component
const SkillTag = ({ skill, accentColor }) => (
    <span className={`inline-block bg-gray-700 text-${accentColor}-400 text-sm font-medium px-4 py-2 rounded-full border border-gray-600 transition-all duration-200 hover:bg-gray-600 shadow-md`}>
      {skill}
    </span>
);


// --- Section Components ---

/**
 * Header (Navigation) Component - Updated for all 8 sections
 */
const Header = ({ name, accentColor, onNavigate }) => {
  const [isOpen, setIsOpen] = useState(false);
  const color500 = accentColor + '-500';

  // UPDATED: Navigation items, ensuring 'Skills' is used
  const navItems = [
    { name: 'Home', id: 'home' },
    { name: 'About Me', id: 'about' }, 
    { name: 'Education', id: 'education' }, 
    { name: 'Skills', id: 'skills' }, 
    { name: 'Internship', id: 'internship' }, 
    { name: 'Projects', id: 'projects' },
    { name: 'Certifications', id: 'certifications' }, 
    { name: 'Contact', id: 'contact' },
  ];

  const handleNavigate = (id) => {
    onNavigate(id);
    setIsOpen(false);
  };

  return (
    <header className="fixed top-0 left-0 w-full z-30 bg-gray-900/95 backdrop-blur-sm shadow-xl border-b border-gray-800">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-16">
          {/* Logo/Name */}
          <div className="flex items-center">
            <Code className={`w-6 h-6 text-${color500} mr-2`} />
            <span className="text-xl font-bold text-white tracking-wider cursor-pointer" onClick={() => handleNavigate('home')}>
              {name.split(' ')[0]}
              <span className={`text-${color500}`}>.dev</span>
            </span>
          </div>

          {/* Desktop Navigation */}
          <nav className="hidden lg:flex space-x-4">
            {navItems.map((item) => (
              <a
                key={item.id}
                href={`#${item.id}`}
                onClick={(e) => { e.preventDefault(); handleNavigate(item.id); }}
                className={`text-gray-300 hover:text-${color500} font-medium transition-colors duration-200 text-sm`}
              >
                {item.name}
              </a>
            ))}
          </nav>

          {/* Mobile Menu Button */}
          <div className="lg:hidden">
            <button
              onClick={() => setIsOpen(!isOpen)}
              className={`p-2 rounded-md text-gray-400 hover:text-white hover:bg-gray-800 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-gray-800 focus:ring-${color500}`}
              aria-expanded={isOpen}
              aria-label="Toggle navigation"
            >
              {isOpen ? <X className="w-6 h-6" /> : <Menu className="w-6 h-6" />}
            </button>
          </div>
        </div>
      </div>

      {/* Mobile Menu Panel */}
      {isOpen && (
        <div className={`lg:hidden bg-gray-900/90 border-t border-gray-800 shadow-lg pb-2`}>
          <div className="px-2 pt-2 pb-3 space-y-1 sm:px-3">
            {navItems.map((item) => (
              <a
                key={item.id}
                href={`#${item.id}`}
                onClick={(e) => { e.preventDefault(); handleNavigate(item.id); }}
                className={`block px-3 py-2 rounded-md text-base font-medium text-gray-300 hover:text-${color500} hover:bg-gray-800 transition-colors duration-200`}
              >
                {item.name}
              </a>
            ))}
          </div>
        </div>
      )}
    </header>
  );
};


/**
 * ABOUT ME Section
 */
const AboutMe = ({ bio, accentColor }) => {
    const color500 = accentColor + '-500';

    return (
        <section id="about" className="py-20 bg-gray-900 border-t border-gray-800">
            <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
                <SectionHeader title="About Me" accentColor={accentColor} Icon={Lightbulb} />
                
                <div className="max-w-4xl mx-auto bg-gray-800 p-8 rounded-xl shadow-lg text-left border border-gray-700">
                    <p className="text-xl text-gray-300 leading-relaxed mb-6">
                        {bio}
                    </p>
                    
                    <h3 className={`text-2xl font-bold text-white mb-4 border-b border-gray-700 pb-2 flex items-center`}>
                        <CheckCircle className={`w-5 h-5 mr-2 text-${color500}`} />
                        My Focus
                    </h3>
                    <ul className="text-gray-400 space-y-2 text-md list-none pl-0">
                        <li className="flex items-start"><CheckCircle className={`w-4 h-4 mt-1 mr-2 flex-shrink-0 text-${color500}`} /> Developing robust data pipelines using Python (Pandas, NumPy).</li>
                        <li className="flex items-start"><CheckCircle className={`w-4 h-4 mt-1 mr-2 flex-shrink-0 text-${color500}`} /> Designing effective data models and optimizing SQL queries for performance.</li>
                        <li className="flex items-start"><CheckCircle className={`w-4 h-4 mt-1 mr-2 flex-shrink-0 text-${color500}`} /> Translating complex data into clear, actionable insights via Power BI dashboards.</li>
                    </ul>
                </div>
            </div>
        </section>
    );
};


/**
 * EDUCATION Section (Academy History) - Divided into two columns
 */
const EducationSection = ({ education, accentColor }) => {
    const degree = education.find(item => item.category === 'degree');
    const puc = education.find(item => item.category === 'puc');

    return (
        <section id="education" className="py-20 bg-gray-900 border-t border-gray-800">
            <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
                <SectionHeader title="Education" accentColor={accentColor} Icon={GraduationCap} />
                
                <div className="max-w-5xl mx-auto grid grid-cols-1 md:grid-cols-2 gap-8">
                    {/* Degree Information (Col 1) */}
                    {degree && (
                        <div className="space-y-4">
                            <h3 className={`text-2xl font-bold text-${accentColor}-400 mb-4 text-left`}>Degree (BCA)</h3>
                            <TimelineItem item={degree} accentColor={accentColor} />
                        </div>
                    )}
                    
                    {/* PUC Information (Col 2) */}
                    {puc && (
                        <div className="space-y-4">
                            <h3 className={`text-2xl font-bold text-${accentColor}-400 mb-4 text-left`}>Pre-University (PUC)</h3>
                            <TimelineItem item={puc} accentColor={accentColor} />
                        </div>
                    )}
                </div>
            </div>
        </section>
    );
};


/**
 * SKILLS Section (Core Technical Skills)
 */
const SkillsSection = ({ skills, accentColor }) => {
    const color500 = accentColor + '-500';
    return (
        <section id="skills" className="py-20 bg-gray-900 border-t border-gray-800">
            <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
                <SectionHeader title="Skills" accentColor={accentColor} Icon={HardHat} /> 
                
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8 max-w-7xl mx-auto">
                    {Object.entries(skills).map(([category, items]) => (
                        <div 
                          key={category} 
                          className={`bg-gray-800 p-6 rounded-xl shadow-2xl text-left border border-gray-700 border-t-4 border-${color500} transition-all duration-300 hover:scale-[1.02]`}
                        >
                            <h3 className={`text-xl font-bold mb-4 text-${color500}`}>{category}</h3>
                            <div className="flex flex-wrap gap-2">
                                {items.map(skill => <SkillTag key={skill} skill={skill} accentColor={accentColor} />)}
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </section>
    );
};


/**
 * INTERNSHIP EXPERIENCE Section
 */
const InternshipExperience = ({ internships, accentColor }) => {
    return (
        <section id="internship" className="py-20 bg-gray-900 border-t border-gray-800">
            <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
                <SectionHeader title="Internship Experience" accentColor={accentColor} Icon={Briefcase} />
                <div className="max-w-4xl mx-auto space-y-8">
                    {internships.map((item) => (
                        <TimelineItem key={item.id} item={item} accentColor={accentColor} />
                    ))}
                </div>
            </div>
        </section>
    );
};


/**
 * PROJECTS Section
 */
const Projects = ({ projects, accentColor }) => {
    const color500 = accentColor + '-500';

    const ProjectCard = ({ project }) => {
      const IconComponent = project.icon;

      return (
        // Force card to take full width on mobile, and half on md/lg screens
        <div className="w-full md:w-1/2 lg:w-1/2 p-0"> 
          <div className="bg-gray-800 rounded-xl overflow-hidden shadow-2xl transition-all duration-300 transform hover:scale-[1.02] hover:shadow-2xl border border-gray-700 h-full">
            <div className={`p-6 border-t-4 border-${color500} h-full flex flex-col`}>
              <div className="flex items-start justify-between mb-4">
                <h3 className="text-2xl font-bold text-white">{project.title}</h3>
                {IconComponent && <IconComponent className={`w-8 h-8 text-${color500} flex-shrink-0`} />}
              </div>
              <p className="text-gray-300 mb-4 h-20 overflow-hidden line-clamp-3 flex-grow">{project.description}</p>
              
              <div className="flex flex-wrap gap-2 mb-6 mt-auto"> {/* mt-auto pushes tags and buttons down */}
                {project.tags.map((tag) => (
                  <span key={tag} className={`text-xs font-semibold px-3 py-1 rounded-full bg-gray-700 text-${accentColor}-400`}>
                    {tag}
                  </span>
                ))}
              </div>

              <div className="flex space-x-3">
                <a
                  href={project.githubUrl}
                  target="_blank"
                  rel="noopener noreferrer"
                  className={`flex items-center space-x-1 px-4 py-2 rounded-lg bg-gray-700 text-gray-100 hover:bg-gray-600 transition-colors duration-200 text-sm font-medium`}
                >
                  <Github className="w-4 h-4" />
                  <span>Code</span>
                </a>
                {project.demoUrl !== '#' && (
                  <a
                    href={project.demoUrl}
                    target="_blank"
                    rel="noopener noreferrer"
                    className={`flex items-center space-x-1 px-4 py-2 rounded-lg bg-${color500} text-white hover:bg-${accentColor}-600 transition-colors duration-200 text-sm font-medium`}
                  >
                    <Zap className="w-4 h-4" />
                    <span>Demo</span>
                  </a>
                )}
              </div>
            </div>
          </div>
        </div>
      );
    };

    return (
      <section id="projects" className="py-20 bg-gray-900 border-t border-gray-800">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
          <SectionHeader title="Projects" accentColor={accentColor} Icon={Code} />
          
          {projects.length > 0 ? (
            <div className="flex flex-wrap justify-center gap-10">
              {projects.map((project) => (
                <ProjectCard key={project.id} project={project} />
              ))}
            </div>
          ) : (
            <div className={`p-6 bg-gray-800 border border-yellow-500 rounded-lg max-w-lg mx-auto`}>
              <AlertTriangle className="w-8 h-8 text-yellow-400 mx-auto mb-3" />
              <p className="text-lg text-yellow-300">No projects found.</p>
              <p className="text-sm text-gray-400 mt-2">Check your API or verify the mock data is being used.</p>
            </div>
          )}
        </div>
      </section>
    );
};


/**
 * CERTIFICATIONS Section
 */
const CertificationsSection = ({ certifications, accentColor }) => {
    const color500 = accentColor + '-500';

    const CertificationCard = ({ cert }) => (
        <a 
            href={cert.link === '#' ? '#' : cert.link} 
            target="_blank" 
            rel="noopener noreferrer"
            className={`block p-6 bg-gray-800 rounded-xl shadow-lg border-l-4 border-transparent transition-all duration-300 hover:border-${color500} hover:bg-gray-700/50 group border border-gray-700`}
            onClick={(e) => cert.link === '#' && e.preventDefault()} // Prevent navigation if link is placeholder
        >
            <div className="flex items-start justify-between">
                <h3 className="text-xl font-semibold text-white group-hover:text-${color500} transition-colors">
                    {cert.name}
                </h3>
                <Award className={`w-6 h-6 flex-shrink-0 text-${color500}`} />
            </div>
            <p className="text-sm text-gray-400 mt-2">
                <span className="font-medium text-gray-300">Issued by:</span> {cert.issuer}
            </p>
            <p className="text-xs text-gray-500 mt-1">
                Completed: {cert.date}
            </p>
            {cert.link !== '#' && (
                <p className={`text-sm mt-3 font-medium text-${color500} flex items-center`}>
                    View Credential <ChevronsRight className="w-4 h-4 ml-1" />
                </p>
            )}
        </a>
    );

    return (
        <section id="certifications" className="py-20 bg-gray-900 border-t border-gray-800">
            <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
                <SectionHeader title="Certifications" accentColor={accentColor} Icon={Award} />
                <p className="text-lg text-gray-400 mb-10 max-w-3xl mx-auto">
                    Formal credentials demonstrating specialized knowledge in key technological areas.
                </p>
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8 max-w-7xl mx-auto">
                    {certifications.map((cert) => (
                        <CertificationCard key={cert.id} cert={cert} />
                    ))}
                </div>
            </div>
        </section>
    );
};


/**
 * CONTACT Section
 */
const Contact = ({ accentColor, email }) => {
  const color500 = accentColor + '-500';

  // State for form submission simulation
  const [formData, setFormData] = useState({ name: '', email: '', message: '' });
  const [status, setStatus] = useState('');

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setStatus('Sending...');

    // Simulating a successful network request
    setTimeout(() => {
      console.log('Form Data:', formData);
      setStatus('Message Sent! I will be in touch soon.');
      setFormData({ name: '', email: '', message: '' });
    }, 1500);
  };

  return (
    <section id="contact" className="py-20 bg-gray-900 border-t border-gray-800">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
        <SectionHeader title="Contact" accentColor={accentColor} Icon={Send} />
        
        <div className="flex flex-col lg:flex-row gap-12 lg:gap-20">
            {/* Contact Info */}
            <div className="lg:w-1/3 flex flex-col justify-start items-center lg:items-start text-center lg:text-left space-y-8 p-6 bg-gray-800 rounded-xl shadow-lg border border-gray-700">
                <p className="text-lg text-gray-300">
                    Feel free to reach out to discuss data projects, potential collaborations, or just to say hello!
                </p>
                
                <div className="space-y-4 w-full">
                    <div className="flex items-center space-x-4">
                        <Mail className={`w-6 h-6 text-${color500} flex-shrink-0`} />
                        <a 
                            href={`mailto:${email}`} 
                            className="text-gray-200 hover:text-white transition-colors duration-200"
                        >
                            {email}
                        </a>
                    </div>
                    <div className="flex items-center space-x-4">
                        <Linkedin className={`w-6 h-6 text-${color500} flex-shrink-0`} />
                        <a href="https://www.linkedin.com/in/kusuma-v" target="_blank" rel="noopener noreferrer" className="text-gray-200 hover:text-white transition-colors duration-200">
                            Connect on LinkedIn
                        </a>
                    </div>
                    <div className="flex items-center space-x-4">
                        <Github className={`w-6 h-6 text-${color500} flex-shrink-0`} />
                        <a href="https://github.com/kusuma-13" target="_blank" rel="noopener noreferrer" className="text-gray-200 hover:text-white transition-colors duration-200">
                            View GitHub Profile
                        </a>
                    </div>
                </div>
            </div>

            {/* Contact Form */}
            <div className="lg:w-2/3 bg-gray-800 p-8 rounded-xl shadow-lg text-left border border-gray-700">
                <form onSubmit={handleSubmit} className="space-y-6">
                    <div>
                        <label htmlFor="name" className="block text-sm font-medium text-gray-300 mb-1">Name</label>
                        <input
                            type="text"
                            id="name"
                            name="name"
                            value={formData.name}
                            onChange={handleChange}
                            required
                            className="w-full px-4 py-2 bg-gray-700 border border-gray-600 rounded-lg text-white focus:ring-2 focus:ring-${color500} focus:border-${color500} transition-all duration-200"
                        />
                    </div>
                    <div>
                        <label htmlFor="email" className="block text-sm font-medium text-gray-300 mb-1">Email</label>
                        <input
                            type="email"
                            id="email"
                            name="email"
                            value={formData.email}
                            onChange={handleChange}
                            required
                            className="w-full px-4 py-2 bg-gray-700 border border-gray-600 rounded-lg text-white focus:ring-2 focus:ring-${color500} focus:border-${color500} transition-all duration-200"
                        />
                    </div>
                    <div>
                        <label htmlFor="message" className="block text-sm font-medium text-gray-300 mb-1">Message</label>
                        <textarea
                            id="message"
                            name="message"
                            rows="4"
                            value={formData.message}
                            onChange={handleChange}
                            required
                            className="w-full px-4 py-2 bg-gray-700 border border-gray-600 rounded-lg text-white focus:ring-2 focus:ring-${color500} focus:border-${color500} transition-all duration-200"
                        ></textarea>
                    </div>
                    
                    <button
                        type="submit"
                        className={`w-full flex items-center justify-center space-x-2 px-6 py-3 font-semibold rounded-lg bg-${color500} text-white hover:bg-${accentColor}-600 transition-colors duration-200 shadow-xl`}
                    >
                        <Send className="w-5 h-5" />
                        <span>Send Message</span>
                    </button>
                    
                    {status && (
                        <p className={`mt-4 text-center text-sm font-medium ${status.includes('Sent') ? 'text-green-400' : 'text-red-400'}`}>
                            {status}
                        </p>
                    )}
                </form>
            </div>
        </div>
      </div>
    </section>
  );
};


// --- Main Application Component ---
const App = () => {
  const [bio] = useState(MY_BIO);
  const [projects] = useState(MOCK_PROJECTS);
  const [internships] = useState(MOCK_INTERNSHIPS);
  const [education] = useState(MOCK_EDUCATION);
  const [certifications] = useState(MOCK_CERTIFICATIONS); 
  const [skills] = useState(MOCK_SKILLS);

  return (
    <div className="min-h-screen bg-gray-900 text-gray-100 font-sans antialiased">
      
      {/* --- Header/Navigation --- */}
      <Header 
        name={MY_NAME} 
        accentColor={ACCENT_COLOR_CLASS} 
        onNavigate={smoothScroll} 
      />

      {/* --- 1. Home/Hero Section (Section ID: home) --- */}
      <section id="home" className="pt-32 pb-16 min-h-screen flex items-center">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 flex flex-col-reverse md:flex-row items-center space-y-12 md:space-y-0 md:space-x-12">
          
          <div className="flex-1 text-center md:text-left">
            <p className="text-xl text-gray-400 mb-2">Hello World! I'm</p>
            <h1 className="text-5xl sm:text-6xl lg:text-8xl font-extrabold tracking-tight text-white mb-4">
              <span className={`text-${ACCENT_COLOR_CLASS}-500`}>{MY_NAME}</span>
            </h1>
            <h2 className={`text-2xl sm:text-3xl lg:text-4xl font-semibold mb-6 text-${ACCENT_COLOR_CLASS}-400`}>
              {MY_TITLE}
            </h2>
            <p className="text-gray-300 max-w-2xl text-lg mb-8">
              {MY_BIO}
            </p>
            <div className="flex justify-center md:justify-start space-x-4">
              <a
                href="#contact"
                onClick={(e) => { e.preventDefault(); smoothScroll('contact'); }}
                className={`px-6 py-3 font-semibold rounded-lg bg-${ACCENT_COLOR_CLASS}-500 text-white hover:bg-${ACCENT_COLOR_CLASS}-600 transition-colors duration-200 shadow-xl`}
              >
                Get In Touch
              </a>
              <a
                href="https://github.com/kusuma-13"
                target="_blank"
                rel="noopener noreferrer"
                className="px-6 py-3 font-semibold rounded-lg border border-gray-700 text-gray-300 hover:text-white hover:bg-gray-800 transition-colors duration-200"
              >
                <Github className="w-5 h-5 inline mr-2" /> GitHub
              </a>
            </div>
          </div>

          {/* Profile Photo */}
          <div className="flex-shrink-0">
            <div className={`w-64 h-64 sm:w-80 sm:h-80 rounded-full overflow-hidden shadow-2xl transition-all duration-300 transform hover:scale-105 border-4 border-${ACCENT_COLOR_CLASS}-500`}>
              <img 
                src={PROFILE_IMAGE_URL} 
                alt={`${MY_NAME}'s profile`} 
                className="w-full h-full object-cover"
                loading="lazy"
                onError={(e) => { e.target.onerror = null; e.target.src = "https://placehold.co/400x400/273a5a/FFFFFF?text=KV+Profile" }}
              />
            </div>
          </div>
        </div>
      </section>

      {/* --- 2. ABOUT ME SECTION (Section ID: about) --- */}
      <AboutMe 
          bio={bio}
          accentColor={ACCENT_COLOR_CLASS}
      />

      {/* --- 3. EDUCATION SECTION (Academy History) (Section ID: education) --- */}
      <EducationSection
          education={education}
          accentColor={ACCENT_COLOR_CLASS}
      />
      
      {/* --- 4. SKILLS SECTION (Core Technical Skills) (Section ID: skills) --- */}
      <SkillsSection
          skills={skills}
          accentColor={ACCENT_COLOR_CLASS}
      />

      {/* --- 5. INTERNSHIP EXPERIENCE SECTION (Section ID: internship) --- */}
      <InternshipExperience 
          internships={internships} 
          accentColor={ACCENT_COLOR_CLASS} 
      />

      {/* --- 6. PROJECTS SECTION (Section ID: projects) --- */}
      <Projects 
        projects={projects} 
        accentColor={ACCENT_COLOR_CLASS} 
      />

      {/* --- 7. CERTIFICATIONS SECTION (Section ID: certifications) --- */}
      <CertificationsSection 
          certifications={certifications}
          accentColor={ACCENT_COLOR_CLASS}
      />

      {/* --- 8. CONTACT SECTION (Section ID: contact) --- */}
      <Contact 
        accentColor={ACCENT_COLOR_CLASS} 
        email={MY_EMAIL}
      />

      {/* --- Footer --- */}
      <footer className="bg-gray-800 py-6 border-t border-gray-700">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center text-gray-400 text-sm">
          &copy; {new Date().getFullYear()} {MY_NAME}. Built with React and Tailwind CSS.
        </div>
      </footer>
    </div>
  );
};

// Initial Render Logic for React
const container = document.getElementById('root');
if (container) {
  const root = createRoot(container);
  root.render(<App />);
} else {
    // Fallback if the root element isn't found
    console.error("The root element with ID 'root' was not found in the DOM.");
    const body = document.getElementsByTagName('body')[0];
    const rootDiv = document.createElement('div');
    rootDiv.id = 'root';
    body.appendChild(rootDiv);
    const root = createRoot(rootDiv);
    root.render(<App />);
}
