import React, { useState, useEffect } from 'react';
import { createRoot } from 'react-dom/client';
// CRITICAL FIX: All icons used anywhere in this file must be imported here.
import {
  Github, Linkedin, Mail, Code, BarChart, Zap, Send,
  AlertTriangle, GraduationCap, HardHat, ChevronsRight, Briefcase,
  Menu, X, CheckCircle, Award, Calendar, ExternalLink
} from 'lucide-react';

// --- Customizable Variables (Personal Information) ---
const MY_NAME = "Kusuma V";
const MY_TITLE = "Data Analyst Intern";
const MY_BIO =
  "Passionate about leveraging data to solve real-world problems. Experienced in Python, MySQL, and statistical analysis, with a focus on fraud detection and system optimization.";
// **************************************************************************************************************************************************
const PROFILE_IMAGE_URL = "https://placehold.co/200x200/4F46E5/FFFFFF?text=KV"; // Placeholder for profile photo
// **************************************************************************************************************************************************
const ACCENT_COLOR_CLASS = "sky"; // Change 'sky' to 'emerald', 'violet', or 'rose' to change theme
const ACCENT_COLORS = {
    500: `${ACCENT_COLOR_CLASS}-500`,
    400: `${ACCENT_COLOR_CLASS}-400`,
    100: `${ACCENT_COLOR_CLASS}-100`,
};

// --- Mock Data ---

// 1. Academy History (Education)
const qualifications = [
  {
    id: 1,
    title: 'B.E. in Electronics & Communication Engineering',
    institution: 'R.V. College of Engineering, Bangalore',
    date: '2020 - 2024',
    details: 'GPA: 8.5/10 (First Class with Distinction). Key courses: Data Structures, Algorithms, Statistical Methods.',
  },
  {
    id: 2,
    title: 'Pre-University (Class XII)',
    institution: 'St. Joseph\'s PU College',
    date: '2019 - 2020',
    details: 'Score: 95%. Major subjects: Physics, Chemistry, Mathematics, Computer Science.',
  },
];

// 2. Core Technical Skills (Categorized)
const skills = [
    { category: "Programming", items: ["Python (Pandas, NumPy, Scikit-learn)", "SQL (MySQL, PostgreSQL)", "JavaScript (Basic)", "C++"] },
    { category: "Data & BI Tools", items: ["Tableau", "Power BI", "Jupyter Notebooks", "Excel (Advanced)", "Google Sheets"] },
    { category: "Cloud & Ops", items: ["AWS (S3, EC2 - Basic)", "Git/GitHub", "Docker (Basic)", "VS Code"] },
    { category: "Analytical Techniques", items: ["Statistical Analysis", "A/B Testing", "Fraud Detection Models", "Time Series Analysis", "Data Visualization"] }
];

// 3. Certifications
const certifications = [
  {
    id: 1,
    title: 'Data Analyst Professional Certificate',
    issuer: 'Google/Coursera',
    date: 'May 2024',
    link: '#',
  },
  {
    id: 2,
    title: 'Advanced SQL for Data Science',
    issuer: 'LinkedIn Learning',
    date: 'Mar 2024',
    link: '#',
  },
];

// 4. Experience
const experiences = [
  {
    id: 1,
    title: 'Data Analyst Intern',
    company: 'Innovate Solutions Inc.',
    location: 'Bangalore, IN (Remote)',
    date: 'Jan 2024 - Present',
    description: [
      'Developed and implemented a machine learning model to detect fraudulent transactions, reducing false positives by 15%.',
      'Optimized database queries in MySQL, leading to a 20% reduction in reporting latency for key business metrics.',
      'Created interactive dashboards in Tableau to track operational performance and presented findings to senior management.',
    ],
  },
  {
    id: 2,
    title: 'Junior Web Developer (Part-Time)',
    company: 'Tech Startup',
    location: 'Remote',
    date: 'Aug 2023 - Dec 2023',
    description: [
      'Contributed to frontend development using HTML, CSS, and basic JavaScript for two internal tools.',
      'Managed and maintained version control using Git, collaborating with a small team of 3 developers.',
    ],
  },
];

// 5. Projects
const projects = [
  {
    id: 1,
    title: 'Retail Sales Forecasting Model',
    description: 'Developed a time-series forecasting model using ARIMA in Python to predict monthly sales for a major retail chain, achieving 90% accuracy. The model integrates with a PostgreSQL database.',
    technologies: ['Python', 'ARIMA', 'Pandas', 'PostgreSQL'],
    link: '#',
  },
  {
    id: 2,
    title: 'E-commerce User Behavior Analysis',
    description: 'Analyzed clickstream data to identify key drop-off points in the sales funnel. Implemented A/B tests based on findings, resulting in a 5% increase in conversion rate.',
    technologies: ['MySQL', 'Tableau', 'Statistical Testing'],
    link: '#',
  },
];

// --- Utility Components ---

/**
 * Common Header for all sections.
 */
const SectionHeader = ({ icon, title, subtitle, accentColor }) => (
  <div className="flex items-center space-x-3 mb-6">
    <div className={`p-2 rounded-full bg-${accentColor}-500 text-white`}>
      {icon}
    </div>
    <div>
      <h2 className="text-3xl font-extrabold tracking-tight text-gray-900 dark:text-white">
        {title}
      </h2>
      <p className="mt-1 text-base text-gray-500 dark:text-gray-400">{subtitle}</p>
    </div>
  </div>
);

/**
 * Component for rendering a single bullet point/detail.
 */
const DetailListItem = ({ text, accentColor }) => (
  <li className="flex items-start mb-2">
    <ChevronsRight className={`flex-shrink-0 h-5 w-5 mt-1 text-${accentColor}-500`} />
    <span className="ml-3 text-gray-700 dark:text-gray-300">{text}</span>
  </li>
);

/**
 * Component for rendering a tag (technology used).
 */
const Tag = ({ technology, accentColor }) => (
  <span
    className={`px-3 py-1 text-xs font-semibold rounded-md bg-${accentColor}-100 text-${accentColor}-700 dark:bg-${accentColor}-900 dark:text-${accentColor}-300 mr-2 mb-2`}
  >
    {technology}
  </span>
);

// --- Main Section Components ---

/**
 * 1. Header/Bio Section
 */
const Hero = ({ accentColor }) => (
  <header className="bg-gray-900 text-white py-16 px-4 sm:px-6 lg:px-8 shadow-2xl">
    <div className="max-w-7xl mx-auto flex flex-col md:flex-row items-center justify-between">
      <div className="text-center md:text-left">
        <h1 className="text-5xl font-extrabold tracking-tight mb-2 leading-tight">
          {MY_NAME}
        </h1>
        <p className={`text-2xl font-medium text-${accentColor}-400 mb-4`}>{MY_TITLE}</p>
        <p className="max-w-xl text-lg text-gray-300">
          {MY_BIO}
        </p>
        <div className="mt-6 flex justify-center md:justify-start space-x-4">
          <a
            href="#"
            className={`flex items-center space-x-2 text-gray-300 hover:text-${accentColor}-400 transition`}
          >
            <Linkedin className="h-5 w-5" />
            <span>LinkedIn</span>
          </a>
          <a
            href="#"
            className={`flex items-center space-x-2 text-gray-300 hover:text-${accentColor}-400 transition`}
          >
            <Github className="h-5 w-5" />
            <span>GitHub</span>
          </a>
          <a
            href="#"
            className={`flex items-center space-x-2 text-gray-300 hover:text-${accentColor}-400 transition`}
          >
            <Mail className="h-5 w-5" />
            <span>Email</span>
          </a>
        </div>
      </div>
      <div className="mt-8 md:mt-0">
        <img
          className="h-32 w-32 rounded-full border-4 border-white object-cover shadow-xl"
          src={PROFILE_IMAGE_URL}
          alt={`${MY_NAME}'s Profile`}
          onError={(e) => { e.target.onerror = null; e.target.src = "https://placehold.co/200x200/4F46E5/FFFFFF?text=KV" }}
        />
      </div>
    </div>
  </header>
);

/**
 * 2. Professional Experience Section
 */
const Experience = ({ experiences, accentColor }) => (
  <section id="experience" className="py-12 px-4 sm:px-6 lg:px-8 bg-white dark:bg-gray-900">
    <SectionHeader
