/**
 * Main Application Component
 */
const App = () => {
  // State for responsive menu (though not strictly required for a static resume)
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  // Apply dark mode based on preference/setting
  useEffect(() => {
    // This is basic dark mode toggle logic for the demo
    if (window.matchMedia && window.matchMedia('(prefers-color-scheme: dark)').matches) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 dark:bg-gray-900 text-gray-900 dark:text-white font-inter">

      {/* --- Responsive Navigation Bar (for anchor links) --- */}
      <nav className="bg-gray-800 shadow-md sticky top-0 z-10">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-16">
            <div className="flex items-center">
              <div className={`text-xl font-bold text-${ACCENT_COLOR_CLASS}-400`}>{MY_NAME}'s Resume</div>
            </div>
            <div className="hidden md:block">
              <div className="ml-10 flex items-baseline space-x-4">
                {['experience', 'education', 'projects', 'skills', 'contact'].map((section) => (
                  <a
                    key={section}
                    href={`#${section}`}
                    className={`px-3 py-2 rounded-md text-sm font-medium text-gray-300 hover:bg-gray-700 hover:text-white transition capitalize`}
                  >
                    {section.replace('_', ' ')}
                  </a>
                ))}
              </div>
            </div>
            <div className="md:hidden">
              <button
                onClick={() => setIsMenuOpen(!isMenuOpen)}
                className="inline-flex items-center justify-center p-2 rounded-md text-gray-400 hover:text-white hover:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-offset-gray-800 focus:ring-white"
              >
                {isMenuOpen ? <X className="block h-6 w-6" /> : <Menu className="block h-6 w-6" />}
              </button>
            </div>
          </div>
        </div>

        {/* Mobile menu panel */}
        {isMenuOpen && (
          <div className="md:hidden">
            <div className="px-2 pt-2 pb-3 space-y-1 sm:px-3">
              {['experience', 'education', 'projects', 'skills', 'contact'].map((section) => (
                <a
                  key={section}
                  href={`#${section}`}
                  onClick={() => setIsMenuOpen(false)}
                  className={`block px-3 py-2 rounded-md text-base font-medium text-gray-300 hover:bg-gray-700 hover:text-white transition capitalize`}
                >
                  {section.replace('_', ' ')}
                </a>
              ))}
            </div>
          </div>
        )}
      </nav>

      {/* --- Resume Content Sections --- */}
      <main className="max-w-7xl mx-auto pt-6 pb-12">

        {/* --- 1. HERO/HEADER SECTION --- */}
        <Hero accentColor={ACCENT_COLOR_CLASS} />

        {/* --- 2. PROFESSIONAL EXPERIENCE SECTION --- */}
        <Experience
          experiences={experiences}
          accentColor={ACCENT_COLOR_CLASS}
        />

        {/* --- 3. EDUCATION & CERTIFICATIONS SECTION (Academy History) --- */}
        <EducationAndQualification
          qualifications={qualifications}
          certifications={certifications}
          accentColor={ACCENT_COLOR_CLASS}
        />

        {/* --- 4. PROJECTS SECTION --- */}
        <Projects
          projects={projects}
          accentColor={ACCENT_COLOR_CLASS}
        />

        {/* --- 5. SKILLS SECTION (Core Technical Skills) --- */}
        <Skills
            skills={skills}
            accentColor={ACCENT_COLOR_CLASS}
        />

        {/* --- 6. CONTACT SECTION --- */}
        <Contact
          accentColor={ACCENT_COLOR_CLASS}
        />

      </main>

      {/* --- Footer --- */}
      <footer className="bg-gray-800 py-6 border-t border-gray-700">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center text-gray-400 text-sm">
          &copy; {new Date().getFullYear()} {MY_NAME}. Built with React and Tailwind CSS.
        </div>
      </footer>
    </div>
  );
};

// Initial Render Logic for React
const container = document.getElementById('root');
if (container) {
  const root = createRoot(container);
  root.render(<App />);
} else {
    // Fallback if the root element isn't found
    console.error("The root element with ID 'root' was not found in the DOM.");
    const body = document.getElementsByTagName('body')[0];
    body.innerHTML = '<div id="root"></div>' + body.innerHTML;
    const fallbackRoot = createRoot(document.getElementById('root'));
    fallbackRoot.render(<App />);
}
