import React, { useState, useEffect } from 'react';
import { createRoot } from 'react-dom/client';
// CRITICAL FIX: All icons used anywhere in this file must be imported here.
import { 
  Github, Linkedin, Mail, Code, BarChart, Zap, Send, 
  AlertTriangle, GraduationCap, HardHat, ChevronsRight, Briefcase, 
  Menu, X, CheckCircle, Award
} from 'lucide-react'; 

// --- Customizable Variables (Personal Information) ---
const MY_NAME = "Kusuma V";
const MY_TITLE = "Data Analyst Intern";
const MY_BIO =
  "Passionate about leveraging data to solve real-world problems. Experienced in Python, MySQL, and statistical analysis, with a focus on fraud detection and system optimization.";
// **************************************************************************************************************************************************
const PROFILE_IMAGE_URL = "https://github.com/kusuma-13/FUTURE_FS_01/blob/main/frontend/src/assets/profile_photo.jpg?raw=true"; 
// **************************************************************************************************************************************************
const ACCENT_COLOR_CLASS = "sky"; // Change 'sky' to 'emerald', 'violet', or 'rose' to change theme
const ACCENT_COLORS = {
    500: `${ACCENT_COLOR_CLASS}-500`,
    400: `${ACCENT_COLOR_CLASS}-400`,
    100: `${ACCENT_COLOR_CLASS}-100`,
};

// --- Mock Data ---

// 1. EDUCATION (Updated to include Pre-University Commerce)
const EDUCATION = [
    {
        degree: "Bachelor of Computer Applications (BCA)",
        institution: "MLA Academy of Higher Learning, Bengaluru",
        dates: "2023 - 2026 (Expected)",
        details: ["Relevant Coursework: Data Structures, Database Management Systems, Advanced Algorithms, and Web Development Principles."],
    },
    {
        degree: "Pre-University Certification (PUC) - Commerce Stream",
        institution: "JS Pre-University College, Bengaluru (Placeholder - Please Update)",
        dates: "2021 - 2023",
        details: [
            "Core Subjects: **Business Studies**, **Accountancy**, **Statistics**, and **Computer Science**.",
            "Established a strong foundation in commercial operations, financial analysis, and quantitative methods."
        ],
    },
];

// 2. EXPERIENCE
const EXPERIENCE = [
  {
    title: "Data Analytics Intern",
    company: "Rooman Technologies Pvt. Ltd.",
    dates: "September 2025 (1 Month)",
    description: [
      "Assisted in cleaning and preprocessing raw datasets using **Pandas** and **NumPy**, improving data reliability by 15%.",
      "Developed interactive dashboards using **Power BI** for the sales team, reducing report generation time by 2 hours daily.",
      "Wrote and optimized **MySQL** queries for complex data extraction, supporting a system migration project.",
      "Contributed to data quality checks using **Streamlit** to build a lightweight data validation application."
    ],
  },
];

// 3. CERTIFICATIONS
const certifications = [
  { name: "Power BI", issuer: "SimpliLearn", date: "August 2025" },
  { name: "Cloud Application Development", issuer: "Skill India (PMKVY)", date: "Mar 2024" },
  { name: "Digital Marketing Short-term Course", issuer: "Inspyr", date: "April 2024" },
  { name: "Office Automation", issuer: "KSEAB", date: "July 2023" },
];

// 4. PROJECTS
const projects = [
  {
    title: "Transaction Fraud Pattern Analyzer",
    description: "A data analytics project utilizing Python and MySQL to identify and visualize fraudulent transaction patterns within large datasets.",
    tags: ["Python", "MySQL", "Data Analysis", "Pandas"],
    link: "https://github.com/kusuma-13/Beginner_Project.git",
  },
  {
    title: "Job Portal Resume Keyword Analyzer",
    description: "A data analytics project using Python and MYSQL to detect keywords in resumes for job portals, enhancing candidate-job matching efficiency.",
    tags: ["Python", "MYSQL", "Streamlit"],
    link: "https://github.com/kusuma-13/Intermediate_Project.git",
  },
];

// 5. SKILLS (Structured, themed format)
const skills = [
    {
        title: "Data Analysis & Business Intelligence",
        icon: BarChart,
        tags: ["Python (Pandas, NumPy)", "SQL (MySQL)", "Power BI", "Streamlit", "Statistical Analysis", "Data Cleaning"],
    },
    {
        title: "Finance & Quantitative Foundations",
        icon: GraduationCap, 
        tags: ["Accountancy", "Business Studies", "Statistics", "Data Modeling", "Risk Assessment"],
    },
    {
        title: "Software & Development Toolbox",
        icon: Code,
        tags: ["Git & GitHub", "Jupyter Notebooks", "VS Code", "HTML/CSS (Tailwind)", "Cloud Application Development"],
    },
];

// --- Component Definitions (UI Building Blocks) ---

const Header = ({ name, title, bio, imageUrl, accentColor }) => (
  <header className="flex flex-col sm:flex-row items-center justify-between p-6 bg-gray-900 shadow-xl rounded-lg mb-8">
    <div className="flex items-center space-x-4">
      <img
        className="w-24 h-24 rounded-full object-cover border-4 border-white shadow-lg"
        src={imageUrl}
        alt={`${name}'s profile`}
        onError={(e) => { e.target.onerror = null; e.target.src = "https://placehold.co/96x96/4b5563/ffffff?text=KV"; }}
      />
      <div>
        <h1 className="text-4xl font-extrabold text-white tracking-tight">{name}</h1>
        <p className={`text-xl font-medium text-${accentColor}-400 mt-1`}>{title}</p>
      </div>
    </div>
    <div className="mt-4 sm:mt-0 sm:max-w-md text-gray-300">
      <p className="text-sm italic">{bio}</p>
    </div>
  </header>
);

const Contact = ({ accentColor }) => (
  <section className="mt-8 p-6 bg-gray-800 rounded-lg shadow-lg">
    <h2 className={`text-2xl font-bold border-b-2 border-${accentColor}-500 pb-2 text-white flex items-center mb-4`}>
        <Send className={`h-6 w-6 mr-2 text-${accentColor}-400`} />
        Get In Touch
    </h2>
    <div className="flex flex-wrap gap-4 text-gray-300 text-sm">
        <a href="mailto:kusumavkusumav13@gmail.com" className={`flex items-center hover:text-${accentColor}-400 transition`}>
            <Mail className="h-4 w-4 mr-1" />
            kusumavkusumav13@gmail.com
        </a>
        <a href="https://linkedin.com/in/kusuma-v" target="_blank" rel="noopener noreferrer" className={`flex items-center hover:text-${accentColor}-400 transition`}>
            <Linkedin className="h-4 w-4 mr-1" />
            LinkedIn/Kusuma-V
        </a>
        <a href="https://github.com/kusuma-13" target="_blank" rel="noopener noreferrer" className={`flex items-center hover:text-${accentColor}-400 transition`}>
            <Github className="h-4 w-4 mr-1" />
            GitHub/kusuma-13
        </a>
    </div>
  </section>
);

const Section = ({ title, icon: Icon, accentColor, children }) => (
  <section className="mt-8 p-6 bg-gray-800 rounded-lg shadow-lg">
    <h2 className={`text-2xl font-bold border-b-2 border-${accentColor}-500 pb-2 text-white flex items-center mb-4`}>
        <Icon className={`h-6 w-6 mr-2 text-${accentColor}-400`} />
        {title}
    </h2>
    {children}
  </section>
);

const Experience = ({ experience, accentColor }) => (
  <Section title="Internship Experience" icon={Briefcase} accentColor={accentColor}>
    {experience.map((exp, index) => (
      <div key={index} className="mb-4 p-4 border-l-4 border-gray-600 hover:border-gray-500 transition duration-300 rounded-md bg-gray-700">
        <div className="flex justify-between items-start">
          <h3 className="text-xl font-semibold text-white">{exp.title} at {exp.company}</h3>
          <span className={`text-sm font-medium text-${accentColor}-400`}>{exp.dates}</span>
        </div>
        <ul className="list-disc list-outside ml-5 mt-2 text-gray-300">
          {exp.description.map((item, i) => (
            <li key={i} className="text-sm mb-1">{item}</li>
          ))}
        </ul>
      </div>
    ))}
  </Section>
);

// REFACTORED: Education section now enforces a 2-column grid for the education entries themselves
const EducationSection = ({ education, accentColor }) => (
  <Section title="Education" icon={GraduationCap} accentColor={accentColor}>
    {/* Grid for two education entries side-by-side (equal height/width) */}
    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
      {education.map((edu, index) => (
        <div key={index} className="p-4 border-l-4 border-gray-600 hover:border-gray-500 transition duration-300 rounded-md bg-gray-700 h-full flex flex-col">
          <div className="flex justify-between items-start mb-2">
            <h3 className="text-lg font-semibold text-white">{edu.degree}</h3>
            <span className={`text-sm font-medium text-${accentColor}-400 flex-shrink-0`}>{edu.dates}</span>
          </div>
          <p className="text-gray-300 mb-2">{edu.institution}</p>
          {/* flex-grow ensures this section takes up remaining space for equal height */}
          <ul className="list-disc list-outside ml-5 mt-auto text-gray-400 text-xs flex-grow">
            {edu.details.map((item, i) => (
              <li key={i} className="mb-1">{item}</li>
            ))}
          </ul>
        </div>
      ))}
    </div>
  </Section>
);

// NEW: Separate Certifications section
const CertificationsSection = ({ certifications, accentColor }) => (
    <Section title="Certifications" icon={Award} accentColor={accentColor}>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
            {certifications.map((cert, index) => (
                <div key={index} className="p-4 rounded-lg bg-gray-700 border-l-4 border-gray-600 hover:border-gray-500 transition duration-300 h-full">
                    <h3 className="text-lg font-semibold text-white">{cert.name}</h3>
                    <div className="flex justify-between text-sm text-gray-400 mt-1">
                        <span>{cert.issuer}</span>
                        <span className={`font-medium text-${accentColor}-400`}>{cert.date}</span>
                    </div>
                </div>
            ))}
        </div>
    </Section>
);

// Projects component updated for equal height columns
const Projects = ({ projects, accentColor }) => (
  <Section title="Projects" icon={HardHat} accentColor={accentColor}>
    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
      {projects.map((proj, index) => (
        // Added h-full and flex-col for equal height/flexible layout
        <div key={index} className="p-4 bg-gray-700 rounded-lg shadow-md border border-gray-600 hover:border-gray-500 transition h-full flex flex-col"> 
          <h3 className="text-xl font-semibold text-white mb-2 hover:text-sky-400 transition">
            <a href={proj.link} target="_blank" rel="noopener noreferrer">{proj.title}</a>
          </h3>
          {/* Added flex-grow to push tags/links down to the bottom of the card */}
          <div className="text-gray-300 text-sm mb-3 flex-grow">{proj.description}</div> 
          <div className="flex flex-wrap gap-2 mt-auto">
            {proj.tags.map((tag, i) => (
              <span key={i} className={`bg-${accentColor}-600 text-white text-xs font-medium px-3 py-1 rounded-full shadow-md`}>
                {tag}
              </span>
            ))}
          </div>
        </div>
      ))}
    </div>
  </Section>
);

// Skills component uses the structured data
const SkillsAndToolbox = ({ skills, accentColor }) => (
  <Section title="Skills & Toolbox" icon={Zap} accentColor={accentColor}>
    <div className="space-y-6">
      {skills.map((category, index) => {
        const CategoryIcon = category.icon; // Icon is passed as a component/function
        return (
          <div key={index} className="bg-gray-700 p-4 rounded-lg">
            <h3 className={`text-xl font-bold text-white mb-3 flex items-center`}>
              <CategoryIcon className={`h-5 w-5 mr-2 text-${accentColor}-400`} />
              {category.title}
            </h3>
            <div className="flex flex-wrap gap-2">
              {category.tags.map((tag, i) => (
                <span key={i} className={`bg-gray-800 text-gray-300 text-sm font-medium px-4 py-1 rounded-full border border-gray-600 hover:bg-gray-600 transition`}>
                  {tag}
                </span>
              ))}
            </div>
          </div>
        );
      })}
    </div>
  </Section>
);


// --- Main Application Component ---
const App = () => {
  const [experience] = useState(EXPERIENCE);
  const [education] = useState(EDUCATION);
  const [certifications] = useState(certifications);
  const [projects] = useState(projects);
  const [skills] = useState(skills);

  return (
    <div className="min-h-screen bg-gray-900 font-sans p-4 sm:p-8">
      <div id="root" className="max-w-6xl mx-auto shadow-2xl rounded-xl p-4 sm:p-8 bg-gray-900 border border-gray-800">
        
        {/* --- 1. HEADER SECTION --- */}
        <Header 
          name={MY_NAME} 
          title={MY_TITLE} 
          bio={MY_BIO} 
          imageUrl={PROFILE_IMAGE_URL} 
          accentColor={ACCENT_COLOR_CLASS} 
        />

        {/* --- 2. EXPERIENCE SECTION --- */}
        <Experience 
          experience={experience} 
          accentColor={ACCENT_COLOR_CLASS} 
        />

        {/* --- 3. EDUCATION SECTION (Now a two-column grid) --- */}
        <EducationSection
          education={education}
          accentColor={ACCENT_COLOR_CLASS}
        />

        {/* --- 4. PROJECTS SECTION (Now a two-column grid) --- */}
        <Projects 
          projects={projects} 
          accentColor={ACCENT_COLOR_CLASS} 
        />

        {/* --- 5. SKILLS & TOOLBOX SECTION (Technology Tags) --- */}
        <SkillsAndToolbox 
            skills={skills} 
            accentColor={ACCENT_COLOR_CLASS} 
        />
        
        {/* --- 6. CERTIFICATIONS SECTION (Now separate) --- */}
        <CertificationsSection
            certifications={certifications}
            accentColor={ACCENT_COLOR_CLASS}
        />

        {/* --- 7. CONTACT SECTION --- */}
        <Contact 
          accentColor={ACCENT_COLOR_CLASS} 
        />

        {/* --- Footer --- */}
        <footer className="bg-gray-800 py-6 border-t border-gray-700 mt-8 rounded-b-lg">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center text-gray-400 text-sm">
            &copy; {new Date().getFullYear()} {MY_NAME}. Built with React and Tailwind CSS.
          </div>
        </footer>
      </div>
    </div>
  );
};

// Initial Render Logic for React
const container = document.getElementById('root');
if (container) {
  const root = createRoot(container);
  root.render(<App />);
} else {
    // Fallback if the root element isn't found
    console.error("The root element with ID 'root' was not found in the DOM. Ensure your HTML has <div id=\"root\"></div>");
}
