import React, { useState, useEffect } from 'react';
import { createRoot } from 'react-dom/client';
// CRITICAL FIX: All icons used anywhere in this file must be imported here.
import { 
  Github, Linkedin, Mail, Code, BarChart, Zap, Send, 
  AlertTriangle, GraduationCap, HardHat, ChevronsRight, Briefcase, 
  Menu, X, CheckCircle, Award
} from 'lucide-react'; 

// --- Customizable Variables (Personal Information) ---
const MY_NAME = "Kusuma V";
const MY_TITLE = "Data Analyst Intern";
const MY_BIO =
  "Passionate about leveraging data to solve real-world problems. Experienced in Python, MySQL, and statistical analysis, with a focus on fraud detection and system optimization.";
// **************************************************************************************************************************************************
const PROFILE_IMAGE_URL = "https://github.com/kusuma-13/FUTURE_FS_01/blob/main/frontend/src/assets/profile_photo.jpg?raw=true"; 
// **************************************************************************************************************************************************
const ACCENT_COLOR_CLASS = "sky"; // Change 'sky' to 'emerald', 'violet', or 'rose' to change theme
const ACCENT_COLORS = {
    500: `${ACCENT_COLOR_CLASS}-500`,
    400: `${ACCENT_COLOR_CLASS}-400`,
    100: `${ACCENT_COLOR_CLASS}-100`,
};
// --- NEW: Add Email Address Variable ---
const MY_EMAIL = "kusumavkusumav13@gmail.com";

// --- Mock Data ---
const SOCIAL_LINKS = {
  linkedin: "https://www.linkedin.com/in/kusuma-v",
  github: "https://github.com/kusuma-13",
  email: "kusumavkusumav13@gmail.com",
};

// --- Mock Data ---

const MOCK_PROJECTS = [
  {
    id: 1,
    title: "Transaction Fraud Pattern Analyzer",
    description: "A data analytics project utilizing Python and MySQL to identify and visualize fraudulent transaction patterns within large datasets.",
    tags: ["Python", "MySQL", "Data Analysis", "Pandas"],
    githubUrl: "https://github.com/kusuma-13/Beginner_Project.git",
    demoUrl: "#",
    icon: BarChart,
  },
  {
    id: 2,
    title: "Job Portal Resume Keyword Analyzer",
    description: "A data analytics project using Python and MYSQL to detect keywords in resumes for job portals, enhancing candidate-job matching efficiency.",
    tags: ["Python", "MYSQL", "Streamlit"],
    githubUrl: "https://github.com/kusuma-13/Intermediate_Project.git",
    demoUrl: "#",
    icon: Zap,
  },
];

const MOCK_INTERNSHIPS = [
  {
    id: 1,
    type: 'experience', 
    company: "Rooman Technologies Pvt. Ltd.",
    role: "Data Analytics Intern", // Explicitly defined as Internship
    duration: "September 2025 (1 Month)",
    details: [
      "Assisted in cleaning and preprocessing raw datasets using **Pandas** and **NumPy**, improving data reliability by 15%.",
      "Developed interactive dashboards using **Power BI** for the sales team, reducing report generation time by 2 hours daily.",
      "Wrote and optimized **MySQL** queries for complex data extraction, supporting a system migration project.",
      "Contributed to data quality checks using **Streamlit** to build a lightweight data validation application."
    ],
  },
];

const MOCK_EDUCATION = [
  {
    id: 1,
    type: 'education', 
    institution: "MLA Academy of Higher Learning",
    degree: "Bachelor of Computer Applications (BCA)",
    duration: "2023 - 2026 (Expected)",
    details: "Currently pursuing foundational knowledge in computer science, software development principles, and analytical skills. Focus on data structures and database management.",
  },
];

const MOCK_CERTIFICATIONS = [
  { 
    id: 1, 
    name: "Power BI", 
    issuer: "SimpliLearn", 
    date: "August 2025",
  },
  { 
    id: 2, 
    name: "Cloud Application Development", 
    issuer: "Skill India (PMKVY)", 
    date: "Mar 2024",
  },
  { 
    id: 3, 
    name: "Digital Marketing Short-term Course", 
    issuer: "Inspyr", 
    date: "April 2024",
  },
  { 
    id: 4, 
    name: "Office Automation", 
    issuer: "KSEAB", 
    date: "July 2023",
  },
];

const MOCK_SKILLS = {
  "Programming & Scripting": ["Python (Pandas, NumPy)", "SQL (MySQL)"],
  "Data Visualization & BI": ["Power BI", "Streamlit", "Seaborn", "Matplotlib"],
  "Tools & Databases": ["Git/GitHub", "Jupyter Notebooks", "MySQL"],
};

// Data used for the Core Qualifications (Skill Bars)
const MOCK_CORE_QUALIFICATIONS = [
    { title: "Python (Pandas/NumPy)", percentage: 90 },
    { title: "SQL (MySQL/Postgres)", percentage: 85 },
    { title: "Data Analysis & Cleaning", percentage: 95 },
    { title: "Power BI / Dashboarding", percentage: 75 },
];


// --- Helper Functions ---
const smoothScroll = (id) => {
  const element = document.getElementById(id);
  if (element) {
    element.scrollIntoView({ behavior: 'smooth' });
  }
};

// --- 1. Header Component ---
const Header = ({ name, accentColor, onNavigate }) => {
  const [isOpen, setIsOpen] = useState(false);
  const color500 = accentColor + '-500';

  // Updated navigation items
  const navItems = [
    { name: 'Home', id: 'home' },
    { name: 'Internship', id: 'internship' }, 
    { name: 'Academics & Core Skills', id: 'academics-skills' }, 
    { name: 'Certifications', id: 'certifications' }, // New navigation item
    { name: 'Projects', id: 'projects' },
    { name: 'Technical Skills', id: 'skills' }, 
    { name: 'Contact', id: 'contact' },
  ];

  const handleNavigate = (id) => {
    onNavigate(id);
    setIsOpen(false);
  };

  return (
    <header className="fixed top-0 left-0 w-full z-30 bg-gray-900/95 backdrop-blur-sm shadow-xl border-b border-gray-800">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-16">
          {/* Logo/Name */}
          <div className="flex items-center">
            <Code className={`w-6 h-6 text-${color500} mr-2`} />
            <span className="text-xl font-bold text-white tracking-wider cursor-pointer" onClick={() => handleNavigate('home')}>
              {name.split(' ')[0]}
              <span className={`text-${color500}`}>.dev</span>
            </span>
          </div>

          {/* Desktop Navigation */}
          <nav className="hidden lg:flex space-x-6">
            {navItems.map((item) => (
              <a
                key={item.id}
                href={`#${item.id}`}
                onClick={(e) => { e.preventDefault(); handleNavigate(item.id); }}
                className={`text-gray-300 hover:text-${color500} font-medium transition-colors duration-200`}
              >
                {item.name}
              </a>
            ))}
          </nav>

          {/* External Links & Mobile Menu Button */}
          <div className="flex items-center space-x-3">
            <div className="hidden md:flex items-center space-x-3">
                <a href="https://github.com/kusuma-13" target="_blank" rel="noopener noreferrer" 
                className={`p-2 rounded-full text-gray-400 hover:text-white hover:bg-gray-800 transition-colors duration-200 border border-gray-700`}>
                <Github className="w-5 h-5" />
                </a>
                <a href="https://www.linkedin.com/in/kusuma-v" target="_blank" rel="noopener noreferrer" 
                className={`p-2 rounded-full text-gray-400 hover:text-white hover:bg-gray-800 transition-colors duration-200 border border-gray-700`}>
                <Linkedin className="w-5 h-5" />
                </a>
            </div>

            {/* Mobile Menu Button */}
            <div className="lg:hidden">
              <button
                onClick={() => setIsOpen(!isOpen)}
                className={`p-2 rounded-md text-gray-400 hover:text-white hover:bg-gray-800 focus:outline-none focus:ring-2 focus:ring-${color500}`}
                aria-expanded={isOpen}
                aria-label="Toggle navigation"
              >
                {isOpen ? <X className="w-6 h-6" /> : <Menu className="w-6 h-6" />}
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Mobile Menu Panel */}
      {isOpen && (
        <div className={`lg:hidden bg-gray-900/90 border-t border-gray-800 shadow-lg pb-2`}>
          <div className="px-2 pt-2 pb-3 space-y-1 sm:px-3">
            {navItems.map((item) => (
              <a
                key={item.id}
                href={`#${item.id}`}
                onClick={(e) => { e.preventDefault(); handleNavigate(item.id); }}
                className={`block px-3 py-2 rounded-md text-base font-medium text-gray-300 hover:text-${color500} hover:bg-gray-800 transition-colors duration-200`}
              >
                {item.name}
              </a>
            ))}
            <div className={`pt-4 border-t border-gray-700 flex justify-center space-x-4 md:hidden`}>
                <a href="https://github.com/kusuma-13" target="_blank" rel="noopener noreferrer" 
                    className={`p-2 rounded-full text-gray-400 hover:text-white hover:bg-gray-800 transition-colors duration-200 border border-gray-700`}>
                  <Github className="w-6 h-6" />
                </a>
                <a href="https://linkedin.com/in/kusuma-v" target="_blank" rel="noopener noreferrer" 
                    className={`p-2 rounded-full text-gray-400 hover:text-white hover:bg-gray-800 transition-colors duration-200 border border-gray-700`}>
                  <Linkedin className="w-6 h-6" />
                </a>
            </div>
          </div>
        </div>
      )}
    </header>
  );
};


// --- Helper Components ---

// Helper component for section headers
const SectionHeader = ({ title, accentColor }) => (
    <h2 className={`text-4xl font-extrabold text-white text-center mb-12 border-b-2 border-${accentColor}-500 inline-block pb-1`}>
      {title}
    </h2>
);

// Component for Experience/Education Item (Timeline)
const TimelineItem = ({ item, accentColor }) => {
    const isExperience = item.type === 'experience';
    const primaryTitle = isExperience ? item.role : item.degree;
    const secondaryTitle = isExperience ? item.company : item.institution;
    const IconComponent = isExperience ? Briefcase : GraduationCap;

    return (
        <div className={`w-full p-4 md:p-6 bg-gray-800 rounded-xl shadow-lg border-l-4 border-${accentColor}-500 transition-all duration-300 hover:shadow-2xl`}>
            <div className="flex items-center justify-between mb-2">
                <h3 className="text-xl font-semibold text-white">{primaryTitle}</h3>
                <IconComponent className={`w-6 h-6 flex-shrink-0 text-${accentColor}-500`} />
            </div>

            <p className={`text-md font-medium text-${accentColor}-400 mt-1`}>{secondaryTitle}</p>
            <p className="text-sm text-gray-400 mt-1 mb-3">{item.duration}</p>
            
            {/* Details */}
            {item.details && Array.isArray(item.details) ? (
                <ul className="text-gray-300 text-sm list-disc pl-5 space-y-1 text-left">
                    {item.details.map((detail, detailIndex) => (
                        <li key={detailIndex} dangerouslySetInnerHTML={{ __html: detail }}></li>
                    ))}
                </ul>
            ) : (
                <p className="text-gray-300 text-sm mt-3">{item.details}</p>
            )}
        </div>
    );
};

// Skill Bar Component
const SkillBar = ({ title, percentage, accentColor }) => {
    return (
      <div className="mb-4">
        <div className="flex justify-between items-center mb-1">
          <span className="text-gray-200 font-medium">{title}</span>
          <span className={`text-sm font-semibold text-${accentColor}-400`}>{percentage}%</span>
        </div>
        <div className="w-full bg-gray-700 rounded-full h-2.5">
          <div 
            className={`h-2.5 rounded-full bg-${accentColor}-500`} 
            style={{ width: `${percentage}%` }}
          ></div>
        </div>
      </div>
    );
  };

// Skill Tag Component
const SkillTag = ({ skill, accentColor }) => (
    <span className={`inline-block bg-gray-700 text-${accentColor}-400 text-sm font-medium px-4 py-2 rounded-full border border-gray-600 transition-all duration-200 hover:bg-gray-600`}>
      {skill}
