// --- Main Application Component ---
export default function App() {
  return (
    <div className="min-h-screen bg-gray-900 font-sans p-4 sm:p-8">
      <div id="resume-container" className="max-w-6xl mx-auto shadow-2xl rounded-xl p-4 sm:p-8 bg-gray-900 border border-gray-800">
        
        {/* --- 1. HEADER SECTION (Full Width) --- */}
        <Header 
          name={MY_NAME} 
          title={MY_TITLE} 
          bio={MY_BIO} 
          imageUrl={PROFILE_IMAGE_URL} 
          accentColor={ACCENT_COLOR_CLASS} 
        />

        {/* --- 2. DISTRIBUTED LAYOUT (Two Columns on Desktop) --- */}
        <div className="grid grid-cols-1 md:grid-cols-3 md:gap-8">
            
            {/* LEFT COLUMN (1/3 Width - Contact, Skills, Certifications) */}
            <div className="md:col-span-1 space-y-8">
                <Contact accentColor={ACCENT_COLOR_CLASS} />
                <SkillsAndToolbox 
                    skills={skills} 
                    accentColor={ACCENT_COLOR_CLASS} 
                />
                <Certifications 
                    certifications={certifications} 
                    accentColor={ACCENT_COLOR_CLASS} 
                />
            </div>

            {/* RIGHT COLUMN (2/3 Width - Experience, Education, Projects) */}
            <div className="md:col-span-2 space-y-8">
                <Experience 
                    experience={EXPERIENCE} 
                    accentColor={ACCENT_COLOR_CLASS} 
                />
                <Education 
                    education={EDUCATION} 
                    accentColor={ACCENT_COLOR_CLASS} 
                />
                <Projects 
                    projects={projects} 
                    accentColor={ACCENT_COLOR_CLASS} 
                />
            </div>
        </div>
        
      </div>
    </div>
  );
};

// Initial Render Logic for React
// Note: This block is usually excluded in production but is kept for canvas compatibility.
const container = document.getElementById('root');
if (container) {
  const root = createRoot(container);
  root.render(<App />);
} else {
    console.error("The root element with ID 'root' was not found in the DOM.");
}
